  {							       \
    s7_pointer p;					       \
    p = car(Args);					       \
    if (Checker(p)) return(Sc->T);			       \
    if (!has_active_methods(sc, p)) return(Sc->F);	       \
    return(apply_boolean_method(Sc, p, Method));	       \
  }

