static void mark_rootlet(s7_scheme *sc)
{
  s7_pointer ge;
  s7_pointer *tmp, *top;

  ge = sc->rootlet;
  tmp = rootlet_elements(ge);
  top = (s7_pointer *)(tmp + sc->rootlet_entries);

  set_mark(ge);
  while (tmp < top)
    gc_mark(slot_value(*tmp++));
  /* slot_setter is handled below with an explicit list -- more code than its worth probably */
  /* we're not marking slot_symbol above which makes me worry that a top-level gensym won't be protected
   *   (apply define (gensym) '(32)), then try to get the GC to clobber {gensym}-0,
   *   but I can't get it to break, so they must be protected somehow; apparently they are
   *   removed from the heap!  At least:
   *   (define-macro (defit) (let ((n (gensym))) `(define (,n) (format #t "fun")))) (defit)
   *   removes the function from the heap (protecting the gensym).
   */
}

/* arrays for permanent_objects are not needed yet: init: cells: 0, lets: 0, s7test: cells: 4, lets: 10, snd-test: cells: 14, lets: 1147 */

static void mark_permanent_objects(s7_scheme *sc)
{
  gc_obj *g;
  for (g = sc->permanent_objects; g; g = (gc_obj *)(g->nxt))
    gc_mark(g->p);
  for (g = sc->permanent_lets; g; g = (gc_obj *)(g->nxt))
    if (is_let(g->p))
      gc_mark(let_outlet(g->p));
}

static void unmark_permanent_objects(s7_scheme *sc)
{
  gc_obj *g;
  for (g = sc->permanent_objects; g; g = (gc_obj *)(g->nxt))
    clear_mark(g->p);
  for (g = sc->permanent_lets; g; g = (gc_obj *)(g->nxt))
    {
      if (!is_marked(g->p))
	{
	  /* TODO: mark that we've cleared let? */
	  if (is_slot(g->p)) slot_set_value(g->p, sc->F);
	  if (is_let(g->p))
	    {
	      s7_pointer slot;
	      for (slot = let_slots(g->p); tis_slot(slot); slot = next_slot(slot))
		slot_set_value(slot, sc->F);
	    }
	}
      clear_mark(g->p);
    }
  /* this is the last thing in gc, so: */
#if S7_DEBUGGING
  for (g = sc->permanent_lets; g; g = (gc_obj *)(g->nxt))
    {
      if (unchecked_type(g->p) == T_FREE)
	fprintf(stderr, "permanent let %p is free\n", g->p);
      else
	{
	  if ((is_let(g->p)) && (unchecked_type(g->p->object.envr.nxt) == T_FREE))
	    fprintf(stderr, "permanent outlet %p is free\n", g->p->object.envr.nxt);
	}
    }
#endif
  /* used to clear_mark the history lists here */
}

static void mark_lamlets(s7_scheme *sc)
{
  s7_int i;
  gc_list *gp;
  gp = sc->lamlets;
  for (i = 0; i < gp->loc; i++)
    {
      s7_pointer s1;
      s1 = gp->list[i];
      if (!is_free_and_clear(s1))
	{
	  s7_pointer lt;
	  lt = opt3_lamlet(s1);
	  if ((is_let(lt)) && (!is_marked(lt)) && (slots_set(lt)))
	    {
	      s7_pointer slot;
	      slot = let_slots(lt);
	      if (is_closure(slot_value(slot)))
		{
		  for (slot = let_slots(closure_let(slot_value(slot))); tis_slot(slot); slot = next_slot(slot))
		    slot_set_value(slot, sc->F);
		}
	      clear_slots_set(lt);
	    }
	  gc_mark(lt);
	}
    }
}


