s7_pointer s7_make_permanent_string(s7_scheme *sc, const char *str)
{
  /* for the symbol table which is never GC'd */
  s7_pointer x;
  x = alloc_pointer(sc);
#if S7_DEBUGGING
  permanent_strings++;
#endif
  set_type(x, T_STRING | T_IMMUTABLE | T_UNHEAP);
  if (str)
    {
      s7_int len;
      len = safe_strlen(str);
      string_length(x) = len;
      string_block(x) = NULL;
      string_value(x) = (char *)permalloc(sc, len + 1);
      memcpy((void *)string_value(x), (void *)str, len);
      string_value(x)[len] = 0;
    }
  else
    {
      string_value(x) = NULL;
      string_length(x) = 0;
    }
  string_hash(x) = 0;
  return(x);
}

