#if WITH_MULTITHREAD_CHECKS
typedef struct {
  s7_scheme* sc;
  const int32_t lock_count; /* Remember lock count in case we have skipped calls to leave_track_scope by longjmp-ing. */
} lock_scope_t;

static lock_scope_t enter_lock_scope(s7_scheme *sc)
{
  int result = pthread_mutex_trylock(&sc->lock);
  if (result != 0)
    {
      fprintf(stderr, "pthread_mutex_trylock failed: %d (EBUSY: %d)", result, EBUSY);
      abort();
    }

  sc->lock_count++;
  {
    lock_scope_t st = {.sc = sc, .lock_count = sc->lock_count};
    return(st);
  }
}

static void leave_lock_scope(lock_scope_t *st)
{
  while (st->sc->lock_count > st->lock_count)
    {
      st->sc->lock_count--;
      pthread_mutex_unlock(&st->sc->lock);
    }
}

#define TRACK(Sc) lock_scope_t lock_scope __attribute__ ((__cleanup__(leave_lock_scope))) = enter_lock_scope(Sc)
#else
#define TRACK(Sc)
#endif

/* various changes in this section courtesy of Woody Douglass 12-Jul-19 */

static block_t *search_load_path(s7_scheme *sc, const char *name)
{
  s7_pointer lst;

  lst = s7_load_path(sc);
  if (is_pair(lst))
    {
      block_t *b;
      char *filename;
      s7_pointer dir_names;
      /* linux: PATH_MAX: 4096, windows: MAX_PATH: unlimited?, Mac: 1016?, BSD: MAX_PATH_LENGTH: 1024 */
#if MS_WINDOWS || defined(__linux__)
      #define S7_FILENAME_MAX 4096
#else
      #define S7_FILENAME_MAX 1024
#endif
      b = mallocate(sc, S7_FILENAME_MAX);
      filename = (char *)block_data(b);

      for (dir_names = lst; is_pair(dir_names); dir_names = cdr(dir_names))
	{
	  const char *new_dir;
	  new_dir = string_value(car(dir_names));
	  if (new_dir)
	    {
	      filename[0] = '\0';
	      if (new_dir[strlen(new_dir) - 1] == '/')
		catstrs(filename, S7_FILENAME_MAX, new_dir, name, (char *)NULL);
	      else catstrs(filename, S7_FILENAME_MAX, new_dir, "/", name, (char *)NULL);
#ifdef _MSC_VER
	      if (_access(filename, 0) != -1)
		return(b);
#else
	      if (access(filename, F_OK) == 0)
		return(b);
#endif
	    }
	}
      liberate(sc, b);
    }
  return(NULL);
}

#if WITH_C_LOADER
#include <dlfcn.h>

static block_t *full_filename(s7_scheme *sc, const char *filename)
{
  s7_int len;
  char *rtn;
  block_t *block;
  if (filename[0] == '/')
    {
      len = safe_strlen(filename);
      block = mallocate(sc, len);
      rtn = (char *)block_data(block);
      memcpy((void *)rtn, (void *)filename, len);
      rtn[len - 1] = '\0';
    }
  else
    {
      char *pwd = getcwd(NULL, 0); /* docs say this means it will return a new string of the right size */
      len = safe_strlen(pwd) + safe_strlen(filename) + 1;
      block = mallocate(sc, len);
      rtn = (char *)block_data(block);
      if (pwd)
	{
          rtn[0] = '\0';
          catstrs(rtn, len, pwd, "/", filename, (char *)NULL);
          free(pwd);
	}
      else
	{
          memcpy((void *)rtn, (void *)filename, len);
          rtn[len - 1] = '\0';
	}
    }
  return(block);
}

static bool load_shared_object(s7_scheme *sc, const char *fname, s7_pointer let)
{
  /* if fname ends in .so, try loading it as a c shared object
   *   (load "/home/bil/cl/m_j0.so" (inlet 'init_func 'init_m_j0))
   */
  s7_int fname_len;

  fname_len = safe_strlen(fname);
  if ((fname_len > 3) &&
      (local_strcmp((const char *)(fname + (fname_len - 3)), ".so")))
    {
      void *library;
      char *pwd_name = NULL;
      block_t *pname = NULL;

      if ((access(fname, F_OK) == 0) || (fname[0] == '/'))
	{
	  pname = full_filename(sc, fname);
	  pwd_name = (char *)block_data(pname);
	}
      else
	{
	  block_t *searched;
	  searched = search_load_path(sc, fname); /* returns NULL if *load-path* is nil, or if nothing matches */
	  if (searched)
	    {
	      if (((const char *)block_data(searched))[0] == '/')
		pname = searched;
	      else
		{
		  pname = full_filename(sc, (const char *)block_data(searched)); /* this is necessary, at least in Linux -- we can't blithely dlopen whatever is passed to us */
		  liberate(sc, searched);
		}
	      pwd_name = (char *)block_data(pname);
	    }
	  else /* perhaps no *load-path* entries */
	    {
	      pname = full_filename(sc, fname);
	      pwd_name = (char *)block_data(pname);
	    }
	}
      /* else pname is NULL, so use fname -- can this happen? */
#if S7_DEBUGGING
      if (!pname) fprintf(stderr, "pname is null\n");
#endif
      library = dlopen((pname) ? pwd_name : fname, RTLD_NOW);
      if (library)
	{
	  if (let) /* look for 'init_func in let */
	    {
	      s7_pointer init;

	      init = s7_let_ref(sc, let, make_symbol(sc, "init_func"));
	      if (is_symbol(init))
		{
		  const char *init_name;
		  void *init_func;

		  if (hook_has_functions(sc->load_hook))
		    s7_apply_function(sc, sc->load_hook, list_1(sc, sc->temp6 = s7_make_string(sc, (pname) ? (const char *)pwd_name : fname)));

		  init_name = symbol_name(init);
		  init_func = dlsym(library, init_name);
		  if (init_func)
		    {
		      typedef void *(*dl_func)(s7_scheme *sc);
		      ((dl_func)init_func)(sc);
		      if (pname) liberate(sc, pname);
		      return(true);
		    }
		  s7_warn(sc, 512, "loaded %s, but can't find init_func %s, dlerror: %s, let: %s\n", fname, init_name, dlerror(), display(let));
		  dlclose(library);
		}
	      else s7_warn(sc, 512, "can't load %s: no init function\n", fname);
#if S7_DEBUGGING
	      fprintf(stderr, "init_func trouble in %s, %s\n", fname, display(init));
#endif
	      return(false);
	    }
	}
      else s7_warn(sc, 512, "load %s failed: %s\n", (pname) ? pwd_name : fname, dlerror());
      if (pname) liberate(sc, pname);
    }
  return(false);
}
#endif

#if WITH_GCC
static FILE *expand_cwd(s7_scheme *sc, const char *fname)
{
  /* catch one special case, "~/..." since it causes 99.9% of the "can't load ..." errors */
  if ((fname[0] == '~') &&
      (fname[1] == '/'))
    {
      char *home;
      home = getenv("HOME");
      if (home)
	{
	  block_t *b;
	  char *filename;
	  s7_int len;
	  FILE *fp;

	  len = safe_strlen(fname) + safe_strlen(home) + 1;
	  b = mallocate(sc, len);
	  filename = (char *)block_data(b);
	  filename[0] = '\0';
	  catstrs(filename, len, home, (char *)(fname + 1), (char *)NULL);
	  fp = fopen(filename, "r");
	  liberate(sc, b);

	  return(fp);
	}
    }
  return(NULL);
}
#endif

static FILE *open_file_with_load_path(s7_scheme *sc, const char *fname)
{
  block_t *b;
  b = search_load_path(sc, fname);
  if (b)
    {
      FILE *fp;
      fp = fopen((const char *)block_data(b), "r");
      if ((fp) && (hook_has_functions(sc->load_hook)))
	s7_apply_function(sc, sc->load_hook, list_1(sc, sc->temp6 = s7_make_string(sc, (const char *)block_data(b))));
      liberate(sc, b);
      return(fp);
    }
  return(NULL);
}

static s7_pointer read_scheme_file(s7_scheme *sc, FILE *fp, const char *fname)
{
  s7_pointer port;
  port = read_file(sc, fp, fname, -1, "load");             /* -1 means always read its contents into a local string */
  port_file_number(port) = remember_file_name(sc, fname);
  set_loader_port(port);
  sc->temp6 = port;
  push_input_port(sc, port);
  sc->temp6 = sc->nil;
  return(port);
}

s7_pointer s7_load_with_environment(s7_scheme *sc, const char *filename, s7_pointer e)
{
  /* returns either the value of the load or NULL if filename not found */
  s7_pointer port;
  FILE *fp;
  declare_jump_info();
  TRACK(sc);

  if (e == sc->s7_let) return(NULL);

#if WITH_C_LOADER
  if (load_shared_object(sc, filename, (is_null(e)) ? sc->rootlet : e))
    return(sc->F);
#endif

  if (is_directory(filename))
    return(NULL);
  fp = fopen(filename, "r");
#if WITH_GCC
  if (!fp) fp = expand_cwd(sc, filename);
#endif
  if (fp)
    {
      if (hook_has_functions(sc->load_hook))
	s7_apply_function(sc, sc->load_hook, list_1(sc, sc->temp6 = s7_make_string(sc, filename)));
    }
  else
    {
      fp = open_file_with_load_path(sc, filename);
      if (!fp) return(NULL);
    }
  port = read_scheme_file(sc, fp, filename);

  sc->curlet = e;
  push_stack(sc, OP_LOAD_RETURN_IF_EOF, port, sc->code);

  store_jump_info(sc);
  set_jump_info(sc, LOAD_SET_JUMP);
  if (jump_loc != NO_JUMP)
    {
      if (jump_loc != ERROR_JUMP)
	eval(sc, sc->cur_op);
    }
  else eval(sc, OP_READ_INTERNAL);

  pop_input_port(sc);
  if (is_input_port(port))
    s7_close_input_port(sc, port);

  restore_jump_info(sc);
  if (is_multiple_value(sc->value))
    sc->value = splice_in_values(sc, multiple_value(sc->value));
  return(sc->value);
}

s7_pointer s7_load(s7_scheme *sc, const char *filename)
{
  return(s7_load_with_environment(sc, filename, sc->nil));
}


static s7_pointer g_load(s7_scheme *sc, s7_pointer args)
{
  #define H_load "(load file (let (rootlet))) loads the scheme file 'file'. The 'let' argument \
defaults to the rootlet.  To load into the current environment instead, pass (curlet)."
  #define Q_load s7_make_signature(sc, 3, sc->values_symbol, sc->is_string_symbol, sc->is_let_symbol)

  FILE *fp = NULL;
  s7_pointer name;
  const char *fname;

  name = car(args);
  if (!is_string(name))
    return(method_or_bust(sc, name, sc->load_symbol, args, T_STRING, 1));

  if (is_pair(cdr(args)))
    {
      s7_pointer e;
      e = cadr(args);
      if (!is_let(e))
	return(wrong_type_argument_with_type(sc, sc->load_symbol, 2, e, a_let_string));
      if (e == sc->s7_let)
	return(s7_error(sc, sc->wrong_type_arg_symbol, set_elist_2(sc, wrap_string(sc, "can't load ~S into *s7*", 23), name)));
      if (e == sc->rootlet)
	sc->curlet = sc->nil;
      else sc->curlet = e;
    }
  else sc->curlet = sc->nil;

  fname = string_value(name);
  if ((!fname) || (!(*fname)))                 /* fopen("", "r") returns a file pointer?? */
    return(s7_error(sc, sc->out_of_range_symbol, set_elist_2(sc, wrap_string(sc, "load's first argument, ~S, should be a filename", 47), name)));

  if (is_directory(fname))
    return(s7_error(sc, sc->wrong_type_arg_symbol, set_elist_2(sc, wrap_string(sc, "load argument, ~S, is a directory", 33), name)));

#if WITH_C_LOADER
  if (load_shared_object(sc, fname, (is_null(sc->curlet)) ? sc->rootlet : sc->curlet))
    return(sc->F);
#endif

  fp = fopen(fname, "r");
#if WITH_GCC
  if (!fp) fp = expand_cwd(sc, fname);
#endif
  if (fp)
    {
      if (hook_has_functions(sc->load_hook))
	s7_apply_function(sc, sc->load_hook, list_1(sc, sc->temp6 = s7_make_string(sc, fname)));
    }
  else
    {
      fp = open_file_with_load_path(sc, fname);
      if (!fp) return(file_error(sc, "load", "can't open", fname));
    }
  read_scheme_file(sc, fp, fname);

  push_stack_op_let(sc, OP_LOAD_CLOSE_AND_POP_IF_EOF);  /* was pushing args and code, but I don't think they're used later */
  push_stack_op_let(sc, OP_READ_INTERNAL);

  return(sc->unspecified);
}


