  /* if ((is_pair(tree)) && (c_callee(tree) == fx_num_eq_ss) && (no_p_in(sc, cadr(p), e1, e2))) fprintf(stderr, "num_eq sW\n"); */
  if ((is_pair(tree)) && (c_callee(tree) == fx_vref_st) && (no_p_in(sc, cadr(p), e1, e2)))
    return(with_c_call(tree, fx_c_Wt_vref)); /* dup */

