static bool b_ii_ok(s7_scheme *sc, opt_info *opc, s7_pointer s_func, s7_pointer car_x, s7_pointer arg1, s7_pointer arg2)
{
  s7_b_ii_t bif;
  bif = s7_b_ii_function(s_func);
  if (bif)
    {
      opc->v[3].b_ii_f = bif;
      if (is_symbol(arg1))
	{
	  opc->v[1].p = symbol_to_slot(sc, arg1);
	  if (is_symbol(arg2))
	    {
	      opc->v[2].p = symbol_to_slot(sc, arg2);

	      opc->v[0].fb = (bif == lt_b_ii) ? opt_b_ii_ss_lt :
		              ((bif == leq_b_ii) ? opt_b_ii_ss_leq :
			       ((bif == gt_b_ii) ? opt_b_ii_ss_gt :
				((bif == geq_b_ii) ? opt_b_ii_ss_geq :
				 ((bif == num_eq_b_ii) ? opt_b_ii_ss_eq :
				  opt_b_ii_ss))));
	      return(true);
	    }
	  if (is_t_integer(arg2))
	    {
	      opc->v[2].i = integer(arg2);
	      opc->v[0].fb = (bif == lt_b_ii) ? opt_b_ii_sc_lt :
		              ((bif == leq_b_ii) ? opt_b_ii_sc_leq :
			       ((bif == gt_b_ii) ? opt_b_ii_sc_gt :
				((bif == geq_b_ii) ? opt_b_ii_sc_geq :
				 ((bif == num_eq_b_ii) ? opt_b_ii_sc_eq :
				  (((bif == logbit_b_ii) && (integer(arg2) >= 0) && (integer(arg2) < S7_INT_BITS)) ? opt_b_ii_sc_bit :
				   opt_b_ii_sc)))));
	      return(true);
	    }
	  opc->v[10].o1 = sc->opts[sc->pc];
	  if (int_optimize(sc, cddr(car_x)))
	    {
	      opc->v[0].fb = (bif == num_eq_b_ii) ? opt_b_ii_sf_eq : opt_b_ii_sf;
	      opc->v[11].fi = opc->v[10].o1->v[0].fi;
	      return(true);
	    }
	  return(return_false(sc, car_x, __func__, __LINE__));
	}
      if (is_symbol(arg2))
	{
	  opc->v[10].o1 = sc->opts[sc->pc];
	  if (int_optimize(sc, cdr(car_x)))
	    {
	      opc->v[11].fi = opc->v[10].o1->v[0].fi;
	      opc->v[2].p = symbol_to_slot(sc, arg2);
	      opc->v[0].fb = opt_b_ii_fs;
	      return(true);
	    }
	  return(return_false(sc, car_x, __func__, __LINE__));
	}
      opc->v[10].o1 = sc->opts[sc->pc];
      if (int_optimize(sc, cdr(car_x)))
	{
	  opc->v[11].fi = opc->v[10].o1->v[0].fi;
	  if (is_t_integer(arg2))
	    {
	      opc->v[2].i = integer(arg2);
	      opc->v[0].fb = (bif == num_eq_b_ii) ? opt_b_ii_fc_eq : opt_b_ii_fc;
	      return(true);
	    }
	  opc->v[8].o1 = sc->opts[sc->pc];
	  if (int_optimize(sc, cddr(car_x)))
	    {
	      opc->v[9].fi = opc->v[8].o1->v[0].fi;
	      opc->v[0].fb = opt_b_ii_ff;
	      return(true);
	    }
	}
    }
  return(return_false(sc, car_x, __func__, __LINE__));
}

/* -------- b_or|and -------- */
static bool opt_and_bb(opt_info *o)
{
  if (o->v[3].fb(o->v[2].o1))
    return(o->v[11].fb(o->v[10].o1));
  return(false);
}

static bool opt_and_bb1(opt_info *o)
{
  if (o->v[5].fb(o))
    return(o->v[11].fb(o->v[10].o1));
  return(false);
}

static bool opt_and_any_b(opt_info *o)
{
  int32_t i;
  for (i = 0; i < o->v[1].i; i++)
    {
      opt_info *o1;
      o1 = o->v[i + 3].o1;
      if (!o1->v[0].fb(o1))
	return(false);
    }
  return(true);
}

static bool opt_or_bb(opt_info *o)
{
  if (o->v[3].fb(o->v[2].o1))
    return(true);
  return(o->v[11].fb(o->v[10].o1));
}

static bool opt_or_bb1(opt_info *o)
{
  if (o->v[5].fb(o))
    return(true);
  return(o->v[11].fb(o->v[10].o1));
}

static bool opt_or_any_b(opt_info *o)
{
  int32_t i;
  for (i = 0; i < o->v[1].i; i++)
    {
      opt_info *o1;
      o1 = o->v[i + 3].o1;
      if (o1->v[0].fb(o1))
	return(true);
    }
  return(false);
}

static bool opt_b_or_and(s7_scheme *sc, s7_pointer car_x, int32_t len, int32_t is_and)
{
  opt_info *opc;
  s7_pointer p;
  int32_t i;

  opc = alloc_opo(sc);
  if (len == 3)
    {
      opt_info *o1;
      o1 = sc->opts[sc->pc];
      if (bool_optimize_nw(sc, cdr(car_x)))
	{
	  opt_info *o2;
	  o2 = sc->opts[sc->pc];
	  if (bool_optimize_nw(sc, cddr(car_x)))
	    {
	      opc->v[10].o1 = o2;
	      opc->v[11].fb = o2->v[0].fb;
	      if ((o1->v[0].fb == opt_b_dd_ss) || (o1->v[0].fb == opt_b_dd_ss_lt) || (o1->v[0].fb == opt_b_dd_ss_gt) ||
		  (o1->v[0].fb == opt_b_ii_ss) ||
		  (o1->v[0].fb == opt_b_ii_ss_lt) || (o1->v[0].fb == opt_b_ii_ss_gt) || (o1->v[0].fb == opt_b_ii_ss_leq) || (o1->v[0].fb == opt_b_ii_ss_geq) ||
		  (o1->v[0].fb == opt_b_pp_ss) ||
		  (o1->v[0].fb == opt_b_7pp_ss) || (o1->v[0].fb == opt_b_7pp_ss_lt) || (o1->v[0].fb == opt_b_7pp_ss_gt))
		{
		  opc->v[5].fb = o1->v[0].fb;
		  opc->v[0].fb = (is_and) ? opt_and_bb1 : opt_or_bb1;
		  opc->v[1].p = o1->v[1].p;
		  opc->v[2].p = o1->v[2].p;
		  opc->v[3].p = o1->v[3].p;
		  return(true);
		}
	      opc->v[0].fb = (is_and) ? opt_and_bb : opt_or_bb;
	      opc->v[2].o1 = o1;
	      opc->v[3].fb = o1->v[0].fb;
	      return(true);
	    }
	}
      return(return_false(sc, car_x, __func__, __LINE__));
    }
  opc->v[1].i = (len - 1);
  for (i = 0, p = cdr(car_x); (is_pair(p)) && (i < 12); i++, p = cdr(p))
    {
      opc->v[i + 3].o1 = sc->opts[sc->pc];
      if (!bool_optimize_nw(sc, p))
	break;
    }
  if (is_null(p))
    {
      opc->v[0].fb = (is_and) ? opt_and_any_b : opt_or_any_b;
      return(true);
    }
  return(return_false(sc, car_x, __func__, __LINE__));
}

static bool opt_b_and(s7_scheme *sc, s7_pointer car_x, int32_t len) {return(opt_b_or_and(sc, car_x, len, true));}
static bool opt_b_or(s7_scheme *sc, s7_pointer car_x, int32_t len)  {return(opt_b_or_and(sc, car_x, len, false));}


