static s7_pointer opt_do_very_simple(opt_info *o)
{
  /* like simple but step can be direct, v[2].p is a let */
  opt_info *o1;
  s7_int end;
  s7_pointer vp, old_e;
  s7_pointer (*f)(opt_info *o);
  s7_scheme *sc;
  sc = o->sc;
  old_e = sc->curlet;
  push_stack_no_let_no_code(sc, OP_GC_PROTECT, old_e);
  sc->curlet = o->v[2].p;

  vp = slot_value(let_dox_slot1(o->v[2].p));
  if (is_slot(let_dox_slot2_unchecked(o->v[2].p)))
    end = integer(slot_value(let_dox_slot2(o->v[2].p)));
  else end = o->v[3].i;

  o1 = o->v[11].o1;
  integer(vp) = integer(o1->v[0].fp(o1));

  o1 = o->v[10].o1;
  f = o1->v[0].fp;
  if (f == opt_p_pip_ssf)                            /* tref.scm */
    {
      opt_info *o2;
      o2 = o1;
      o1 = o2->v[4].o1;
      while (integer(vp) < end)
	{
	  o2->v[3].p_pip_f(o2->sc, slot_value(o2->v[1].p), integer(slot_value(o2->v[2].p)), o1->v[0].fp(o1));
	  integer(vp)++;
	}
    }
  else
    {
      if (f == opt_p_pip_sso)
	{
	  if ((let_dox_slot1(o->v[2].p) == o1->v[2].p) && (o1->v[2].p == o1->v[4].p))
	    {
	      if (((o1->v[5].p_pip_f == float_vector_set_unchecked_p) &&
		   (o1->v[6].p_pi_f == float_vector_ref_unchecked_p)) ||
		  ((o1->v[5].p_pip_f == int_vector_set_unchecked_p) &&
		   (o1->v[6].p_pi_f == int_vector_ref_unchecked_p)))
		{
		  copy_to_same_type(sc, slot_value(o1->v[1].p), slot_value(o1->v[3].p), integer(vp), end, integer(vp));
		  unstack(sc);
		  sc->curlet = old_e;
		  return(sc->T);
		}
	    }
	  while (integer(vp) < end)
	    {
	      o1->v[5].p_pip_f(o1->sc, slot_value(o1->v[1].p), integer(slot_value(o1->v[2].p)),
			       o1->v[6].p_pi_f(o1->sc, slot_value(o1->v[3].p), integer(slot_value(o1->v[4].p))));
	      integer(vp)++;
	    }
	}
      else
	{
	  if ((f == opt_set_p_i_f) &&                      /* tvect.scm */
	      (is_t_integer(slot_value(o1->v[1].p))) &&
	      (o1->v[1].p != let_dox_slot1(o->v[2].p)))
	    {
	      s7_pointer ival;
	      opt_info *o2;
	      s7_int (*fi)(opt_info *o);
	      ival = make_mutable_integer(sc, integer(slot_value(o1->v[1].p)));
	      slot_set_value(o1->v[1].p, ival);
	      o2 = o1->v[5].o1;                  /* set_p_i_f: x = make_integer(o->sc, o->v[6].fi(o->v[5].o1)); */
	      fi = o2->v[0].fi;
	      while (integer(vp) < end)
		{
		  integer(ival) = fi(o2);
		  integer(vp)++;
		}
	      slot_set_value(o1->v[1].p, make_integer(sc, integer(slot_value(o1->v[1].p))));
	    }
	  else
	    {
	      if ((f == opt_d_7pid_ssf_nr) &&           /* tref.scm */
		  (o1->v[4].d_7pid_f == float_vector_set_unchecked))
		{
		  s7_pointer fv, ind;
		  opt_info *o2;
		  s7_double (*fd)(opt_info *o);
		  o2 = o1->v[10].o1;
		  fv = slot_value(o1->v[1].p);
		  ind = o1->v[2].p;
		  fd = o2->v[0].fd;
		  while (integer(vp) < end)
		    {
		      float_vector_set_unchecked(sc, fv, integer(slot_value(ind)), fd(o2));
		      integer(vp)++;
		    }
		}
	      else
		{
		  while (integer(vp) < end)
		    {
		      f(o1);
		      integer(vp)++;
		    }}}}}
  unstack(sc);
  sc->curlet = old_e;
  return(sc->T);
}

