/* -------------------------------- let* -------------------------------- */
static bool check_let_star(s7_scheme *sc)
{
  s7_pointer vars, form, code;
  bool named_let, fxable = true;

  form = sc->code;
  code = cdr(form);

  if (!is_pair(code))                           /* (let* . 1) */
    eval_error(sc, "let* variable list is messed up: ~A", 35, form);
  if (!is_pair(cdr(code)))                      /* (let* ()) */
    eval_error(sc, "let* has no body: ~A", 20, form);

  named_let = (is_symbol(car(code)));

  if (named_let)
    {
      if (!is_list(cadr(code)))                 /* (let* hi #t) */
	eval_error(sc, "let* variable list is messed up: ~A", 35, form);
      if (!is_pair(cddr(code)))                 /* (let* hi () . =>) or (let* hi () ) */
	{
	  if (is_null(cddr(code)))
	    eval_error(sc, "named let* has no body: ~A", 26, form);
	  else eval_error(sc, "named let* stray dot? ~A", 24, form);
	}
      if (is_constant_symbol(sc, car(code)))
	s7_error(sc, sc->wrong_type_arg_symbol,
		 set_elist_2(sc, wrap_string(sc, "can't bind an immutable object: ~S", 34), form));
      set_local(car(code));
    }
  else
    {
      if (!is_list(car(code)))                      /* (let* x ... ) */
	eval_error(sc, "let* variable declaration value is missing: ~A", 46, form);
    }

  for (vars = ((named_let) ? cadr(code) : car(code)); is_pair(vars); vars = cdr(vars))
    {
      s7_pointer var_and_val, var;
      var_and_val = car(vars);

      if (!is_pair(var_and_val))                    /* (let* (3) ... */
	eval_error(sc, "let* variable list is messed up? ~A", 35, var_and_val);

      /* no check for repeated var (unlike lambda* and named let*) */
      if (!(is_pair(cdr(var_and_val))))             /* (let* ((x . 1))...) */
	{
	  if (is_null(cdr(var_and_val)))
	    eval_error(sc, "let* variable declaration, but no value?: ~A", 44, var_and_val);
	  else eval_error(sc, "let* variable declaration is not a proper list?: ~A", 51, var_and_val);
	}
      if (!is_null(cddr(var_and_val)))              /* (let* ((c 1 2)) ...) */
	eval_error(sc, "let* variable declaration has more than one value?: ~A", 54, var_and_val);

      var = car(var_and_val);

      if (!(is_symbol(var)))                        /* (let* ((3 1)) 1) */
	eval_error(sc, "bad variable ~S in let*", 23, var);

      if (is_constant_symbol(sc, var))              /* (let* ((pi 3)) ...) */
	s7_error(sc, sc->wrong_type_arg_symbol,
		 set_elist_2(sc, wrap_string(sc, "can't bind an immutable object: ~S", 34), var_and_val));

      if ((named_let) && (symbol_is_in_arg_list(var, cdr(vars)))) /* (let* loop ((a 1) (a 2)) ...) -- added 2-Dec-19 */
	eval_error(sc, "named let* parameter '~A is used twice in the parameter list", 60, var);
      /* currently (let* ((a 1) (a (+ a 1))) a) is 2, not an error. */

      set_local(var);
    }
  if (!is_null(vars))
    eval_error(sc, "let* variable list is not a proper list?: ~A", 44, vars);

  if (!s7_is_proper_list(sc, cdr(code)))
    eval_error(sc, "stray dot in let* body: ~S", 26, cdr(code));

  for (vars = (named_let) ? cadr(code) : car(code); is_pair(vars); vars = cdr(vars))
    {
      if (is_fxable(sc, cadar(vars)))
	set_c_call(cdar(vars), fx_choose(sc, cdar(vars), sc->curlet, let_star_symbol_is_safe));
      else fxable = false;
    }

  if (named_let)
    {
      if (is_null(cadr(code)))
	pair_set_syntax_op(form, OP_NAMED_LET_NO_VARS);
      else
	{
	  pair_set_syntax_op(form, OP_NAMED_LET_STAR);
	  set_opt2_con(code, cadr(caadr(code)));
	}
    }
  else
    {
      if (is_null(car(code)))
	pair_set_syntax_op(form, OP_LET_NO_VARS);       /* (let* () ...) */
      else
	{
	  if (is_null(cdar(code)))
	    {
	      check_let_one_var(sc, form, car(code)); /* (let* ((var...))...) -> (let ((var...))...) */
	      if (optimize_op(form) >= OP_LET_FX_OLD)
		{
		  if ((not_in_heap(form)) &&
		      (body_is_safe(sc, sc->unused, cdr(code), true) >= SAFE_BODY))
		    set_opt3_let(code, make_permanent_let(sc, car(code)));
		  else
		    {
		      set_optimize_op(form, optimize_op(form) + 1); /* *_old -> *_new */
		      set_opt3_let(code, sc->nil);
		    }
		}
	    }
	  else  /* multiple variables */
	    {
	      s7_pointer last_var;
	      if (fxable)
		{
		  pair_set_syntax_op(form, OP_LET_STAR_FX);
		  if ((is_null(cddr(code))) &&
		      (is_fxable(sc, cadr(code))))
		    {
		      fx_annotate_arg(sc, cdr(code), sc->curlet);
		      pair_set_syntax_op(form, OP_LET_STAR_FX_A); /* does this ever happen? */
		    }
		}
	      else pair_set_syntax_op(form, OP_LET_STAR2);
	      set_opt2_con(code, cadaar(code));

	      for (last_var = caaar(code), vars = cdar(code); is_pair(vars); last_var = caar(vars), vars = cdr(vars))
		if (has_fx(cdar(vars)))
		  fx_tree(sc, cdar(vars), last_var, NULL);
	    }
	}
    }

  /* let_star_unchecked... */
  if (named_let) /* (is_symbol(car(code))) */
    {
      sc->value = cdr(code);
      if (is_null(car(sc->value)))
	{
	  s7_pointer cx;
	  cx = car(code);
	  sc->curlet = make_let_slowly(sc, sc->curlet);
	  sc->code = T_Pair(cdr(sc->value));
	  make_slot_2(sc, sc->curlet, cx, make_closure(sc, sc->nil, sc->code, T_CLOSURE_STAR, 0));
	  return(false);
	}
    }
  else
    {
      if (is_null(car(code)))
	{
	  sc->curlet = make_let_slowly(sc, sc->curlet);
	  sc->code = T_Pair(cdr(code));
	  return(false);
	}
    }

  if (named_let) /* is_symbol(car(code))) */
    {
      push_stack(sc, OP_LET_STAR1, code, cadr(code));
      sc->code = cadr(caadr(code));
    }
  else
    {
      push_stack(sc, OP_LET_STAR1, code, car(code));
      /* args is the let body, saved for later, code is the list of vars+initial-values */
      sc->code = cadr(caar(code));
      /* caar(code) = first var/val pair, we've checked that all these guys are legit, so cadr of that is the value */
    }
  return(true);
}

static inline bool op_let_star1(s7_scheme *sc)
{
  /* we can't skip (or reuse) this new let -- we have to imitate a nested let, otherwise
   *   (let ((f1 (lambda (arg) (+ arg 1))))
   *     (let* ((x 32)
   *            (f1 (lambda (arg) (f1 (+ x arg)))))
   *       (f1 1)))
   * will hang.
   * To get around this requires find_symbol or s7_tree_memq in check_let_star,
   *   both (much) more expensive than making a useless let!.
   */

  /* TODO: can this be moved to the first call point above? */
  uint64_t let_counter = S7_INT64_MAX;
  while (true)
    {
      if (let_counter == sc->capture_let_counter)
	make_slot_2(sc, sc->curlet, caar(sc->code), sc->value);
      else
	{
	  sc->curlet = make_let_with_slot(sc, sc->curlet, caar(sc->code), sc->value);
	  let_counter = sc->capture_let_counter;
	}

      sc->code = cdr(sc->code);
      if (is_pair(sc->code))
	{
	  s7_pointer x;
	  x = cdar(sc->code);
	  if (has_fx(x))
	    sc->value = fx_call(sc, x);
	  else
	    {
	      push_stack_direct(sc, OP_LET_STAR1);
	      sc->code = car(x);
	      return(true);
	    }
	}
      else break;
    }
  sc->code = sc->args; /* original sc->code set in push_stack above */
  if (is_symbol(car(sc->code)))
    {
      /* now we need to declare the new function */
      s7_pointer body, args;
      body = cddr(sc->code);
      args = cadr(sc->code);
      make_slot_2(sc, sc->curlet, car(sc->code), make_closure(sc, args, body, T_CLOSURE_STAR, (is_null(args)) ? 0 : CLOSURE_ARITY_NOT_SET));
      sc->code = body;
    }
  else sc->code = T_Pair(cdr(sc->code));
  return(false);
}

static void op_let_star_fx(s7_scheme *sc)
{
  /* fx safe does not mean we can dispense with the inner lets (curlet is safe for example) */
  s7_pointer p;
  uint64_t let_counter = S7_INT64_MAX;
  sc->code = cdr(sc->code);
  for (p = car(sc->code); is_pair(p); p = cdr(p))
    {
      s7_pointer val;
      val = fx_call(sc, cdar(p)); /* eval in outer let */
      if (let_counter == sc->capture_let_counter)
	/* make_slot_1(sc, sc->curlet, caar(p), val); */
	{
#if S7_DEBUGGING
	  if (let_id(sc->curlet) < symbol_id(caar(p)))
	    fprintf(stderr, "inversion for %s: %" print_s7_int " %" print_s7_int "\n", display(caar(p)), let_id(sc->curlet), symbol_id(caar(p)));
#endif
	  add_slot_checked(sc, sc->curlet, caar(p), val);
	}
      else
	{
	  sc->curlet = make_let_with_slot(sc, sc->curlet, caar(p), val);
	  let_counter = sc->capture_let_counter;
	}
    }
  sc->code = T_Pair(cdr(sc->code));
}

static void op_let_star_fx_a(s7_scheme *sc)
{
  s7_pointer p;
  uint64_t let_counter = S7_INT64_MAX;
  sc->code = cdr(sc->code);
  for (p = car(sc->code); is_pair(p); p = cdr(p))
    {
      s7_pointer val;
      val = fx_call(sc, cdar(p));
      if (let_counter == sc->capture_let_counter)
	make_slot_2(sc, sc->curlet, caar(p), val);
      else
	{
	  sc->curlet = make_let_with_slot(sc, sc->curlet, caar(p), val);
	  let_counter = sc->capture_let_counter;
	}
    }
  sc->value = fx_call(sc, cdr(sc->code));
}

static void op_named_let_star(s7_scheme *sc)
{
  s7_pointer code;
  code = cdr(sc->code);
  push_stack(sc, OP_LET_STAR1, code, cadr(code));
  sc->code = opt2_con(code);
}

static void op_let_star2(s7_scheme *sc)
{
  s7_pointer code;
  code = cdr(sc->code);
  push_stack(sc, OP_LET_STAR1, code, car(code));
  sc->code = opt2_con(code);
}


/* -------------------------------- letrec, letrec* -------------------------------- */
static void check_letrec(s7_scheme *sc, bool letrec)
{
  s7_pointer x, caller, code;
  code = cdr(sc->code);
  caller = (letrec) ? sc->letrec_symbol : sc->letrec_star_symbol;

  if ((!is_pair(code)) ||                     /* (letrec . 1) */
      (!is_list(car(code))))                  /* (letrec 1 ...) */
    eval_error_with_caller(sc, "~A: variable list is messed up: ~A", 34, caller, sc->code);

  if (!is_pair(cdr(code)))                    /* (letrec ()) */
    eval_error_with_caller(sc, "~A has no body: ~A", 18, caller, sc->code);

  clear_symbol_list(sc);
  for (x = car(code); is_not_null(x); x = cdr(x))
    {
      s7_pointer y, carx;
      if (!is_pair(x))                        /* (letrec ((a 1) . 2) ...) */
	eval_error_with_caller(sc, "~A: improper list of variables? ~A", 34, caller, sc->code);

      carx = car(x);
      if ((!is_pair(carx)) ||                 /* (letrec (1 2) #t) */
	  (!(is_symbol(car(carx)))))
	eval_error_with_caller(sc, "~A: bad variable ~S", 19, caller, carx);

      y = car(carx);
      if (is_constant_symbol(sc, y))
	s7_error(sc, sc->wrong_type_arg_symbol,
		 set_elist_2(sc, wrap_string(sc, "can't bind an immutable object: ~S", 34), x));

      if (!is_pair(cdr(carx)))                /* (letrec ((x . 1))...) */
	{
	  if (is_null(cdr(carx)))             /* (letrec ((x)) x) -- perhaps this is legal? */
	    eval_error_with_caller(sc, "~A: variable declaration has no value?: ~A", 42, caller, carx);
	  eval_error_with_caller(sc, "~A: variable declaration is not a proper list?: ~A", 50, caller, carx);
	}
      if (is_not_null(cddr(carx)))            /* (letrec ((x 1 2 3)) ...) */
	eval_error_with_caller(sc, "~A: variable declaration has more than one value?: ~A", 53, caller, carx);

      /* check for name collisions -- this is needed in letrec* else which of the two legit values does our "rec" refer to, so to speak */
      if (symbol_is_in_list(sc, y))
	eval_error_with_caller(sc, "~A: duplicate identifier: ~A", 28, caller, y);
      add_symbol_to_list(sc, y);
      set_local(y);
    }

  if (!s7_is_proper_list(sc, cdr(code)))
    eval_error_with_caller(sc, "stray dot in ~A body: ~S", 24, caller, cdr(code));

  for (x = car(code); is_pair(x); x = cdr(x))
    if (is_fxable(sc, cadar(x)))
      set_c_call(cdar(x), fx_choose(sc, cdar(x), sc->curlet, let_symbol_is_safe_or_listed));

  pair_set_syntax_op(sc->code, (letrec) ? OP_LETREC_UNCHECKED : OP_LETREC_STAR_UNCHECKED);
}

static s7_pointer make_funclet(s7_scheme *sc, s7_pointer new_func, s7_pointer func_name, s7_pointer outer_let);

static void op_letrec2(s7_scheme *sc)
{
  s7_pointer slot;
  for (slot = let_slots(sc->curlet); tis_slot(slot); slot = next_slot(slot))
    if (is_checked_slot(slot))
      slot_set_value(slot, slot_pending_value(slot));

  for (slot = let_slots(sc->curlet); tis_slot(slot); slot = next_slot(slot))
    if (is_closure(slot_value(slot)))
      {
	s7_pointer func;
	func = slot_value(slot);
	if ((!is_safe_closure(func)) ||
	    (!is_optimized(car(closure_body(func)))))
	  optimize_lambda(sc, true, slot_symbol(slot), closure_args(func), closure_body(func));
	make_funclet(sc, func, slot_symbol(slot), closure_let(func));
      }
}

static bool op_letrec_unchecked(s7_scheme *sc)
{
  s7_pointer code;
  code = cdr(sc->code);
  /*   get all local vars and set to #<undefined>
   *   get parallel list of values
   *   eval each member of values list with let still full of #<undefined>'s
   *   assign each value to its variable
   *   eval body
   * which means that (letrec ((x x)) x) is not an error!
   * but this assumes the environment is not changed by evaluating the exprs?
   * (letrec ((a (define b 1))) b) -- if let, the define takes place in the calling let, not the current let
   * (letrec ((f1 (lambda (x) (f2 (* 2 x))))) (define (f2 y) (- y 1)) (f1 3)) -> 5 (Guile says unbound f2)
   * I think I need to check here that slot_pending_value is set (using the is_checked bit below):
   *   (letrec ((i (begin (define xyz 37) 0))) (curlet)): (inlet 'i 0 'xyz 37) -- is this correct?
   */
  sc->curlet = make_let_slowly(sc, sc->curlet);
  if (is_pair(car(code)))
    {
      s7_pointer x, slot;
      for (x = car(code); is_not_null(x); x = cdr(x))
	{
	  slot = make_slot_2(sc, sc->curlet, caar(x), sc->undefined);
	  slot_set_pending_value(slot, sc->undefined);
	  slot_set_expression(slot, cdar(x));
	  set_checked_slot(slot);
	}
      for (slot = let_slots(sc->curlet); tis_slot(slot) && (has_fx(slot_expression(slot))); slot = next_slot(slot))
	slot_set_pending_value(slot, fx_call(sc, slot_expression(slot)));
      if (tis_slot(slot))
	{
	  push_stack(sc, OP_LETREC1, slot, code);
	  sc->code = car(slot_expression(slot));
	  return(true);
	}
      op_letrec2(sc);
    }
  sc->code = T_Pair(cdr(code));
  return(false);
}

static bool op_letrec1(s7_scheme *sc)
{
  s7_pointer slot;
  slot_set_pending_value(sc->args, sc->value);
  for (slot = next_slot(sc->args); tis_slot(slot) && (has_fx(slot_expression(slot))); slot = next_slot(slot))
    slot_set_pending_value(slot, fx_call(sc, slot_expression(slot)));
  if (tis_slot(slot))
    {
      push_stack(sc, OP_LETREC1, slot, sc->code);
      sc->code = car(slot_expression(slot));
      return(true);
    }
  op_letrec2(sc);
  sc->code = T_Pair(cdr(sc->code));
  return(false);
}


static bool op_letrec_star_unchecked(s7_scheme *sc)
{
  s7_pointer slot, code;
  code = cdr(sc->code);
  /* get all local vars and set to #<undefined>
   * eval each member of values list and assign immediately, as in let*
   * eval body
   */
  sc->curlet = make_let_slowly(sc, sc->curlet);
  if (is_pair(car(code)))
    {
      s7_pointer x;
      for (x = car(code); is_not_null(x); x = cdr(x))
	{
	  slot = make_slot_2(sc, sc->curlet, caar(x), sc->undefined);
	  slot_set_expression(slot, cdar(x));
	}
      let_set_slots(sc->curlet, reverse_slots(sc, let_slots(sc->curlet)));

      for (slot = let_slots(sc->curlet); tis_slot(slot) && (has_fx(slot_expression(slot))); slot = next_slot(slot))
	slot_set_value(slot, fx_call(sc, slot_expression(slot)));
      if (tis_slot(slot))
	{
	  push_stack(sc, OP_LETREC_STAR1, slot, code);
	  sc->code = car(slot_expression(slot));
	  return(true);
	}
    }
  sc->code = T_Pair(cdr(code));
  return(false);
}

static bool op_letrec_star1(s7_scheme *sc)
{
  s7_pointer slot;
  slot = sc->args;
  slot_set_value(slot, sc->value);

  for (slot = next_slot(slot); tis_slot(slot) && (has_fx(slot_expression(slot))); slot = next_slot(slot))
    slot_set_value(slot, fx_call(sc, slot_expression(slot)));
  if (tis_slot(slot))
    {
      push_stack(sc, OP_LETREC_STAR1, slot, sc->code);
      sc->code = car(slot_expression(slot));
      return(true);
    }

  for (slot = let_slots(sc->curlet); tis_slot(slot); slot = next_slot(slot))
    if (is_closure(slot_value(slot)))
      {
	s7_pointer func;
	func = slot_value(slot);
	if ((!is_safe_closure(func)) ||
	    (!is_optimized(car(closure_body(func)))))
	  optimize_lambda(sc, true, slot_symbol(slot), closure_args(func), closure_body(func));
	make_funclet(sc, slot_value(slot), slot_symbol(slot), closure_let(slot_value(slot)));
      }

  sc->code = T_Pair(cdr(sc->code));
  return(false);
}


/* -------------------------------- let-temporarily -------------------------------- */
static void check_let_temporarily(s7_scheme *sc)
{
  s7_pointer x, form, code;
  bool all_fx, all_s7;
  form = sc->code;
  code = cdr(form);

  if ((!is_pair(code)) ||                 /* (let-temporarily . 1) */
      (!is_list(car(code))))              /* (let-temporarily 1 ...) */
    eval_error(sc, "let-temporarily: variable list is messed up: ~A", 47, form);
  /* cdr(code) = body can be nil */

  all_fx = is_pair(car(code));
  all_s7 = all_fx;

  for (x = car(code); is_not_null(x); x = cdr(x))
    {
      s7_pointer carx;
      if (!is_pair(x))                        /* (let-temporarily ((a 1) . 2) ...) */
	eval_error(sc, "let-temporarily: improper list of variables? ~A", 47, form);

      carx = car(x);
      if (!is_pair(carx))                     /* (let-temporarily (1 2) #t) */
	eval_error(sc, "let-temporarily: bad variable ~S", 32, carx);

      if (is_symbol(car(carx)))
	{
	  if (is_constant_symbol(sc, car(carx))) /* (let-temporarily ((pi 3)) ...) */
	    s7_error(sc, sc->wrong_type_arg_symbol,
		     set_elist_2(sc, wrap_string(sc, "can't bind an immutable object: ~S", 34), x));
	  if (is_syntactic_symbol(car(carx)))    /* (let-temporarily ((if 3)) ...) */
	    s7_error(sc, sc->wrong_type_arg_symbol,
		     set_elist_2(sc, wrap_string(sc, "can't set! ~S", 13), car(carx)));
	}
      else
	{
	  if (!is_pair(car(carx)))            /* (let-temporarily ((1 2)) ...) */
	    eval_error(sc, "let-temporarily: bad variable ~S", 32, carx);
	}

      if (!is_pair(cdr(carx)))                /* (let-temporarily ((x . 1))...) */
	eval_error(sc, "let-temporarily: variable declaration value is messed up: ~S", 60, carx);

      if (is_not_null(cddr(carx)))            /* (let-temporarily ((x 1 2 3)) ...) */
	eval_error(sc, "let-temporarily: variable declaration has more than one value?: ~A", 66, carx);

      if ((all_fx) &&
	  ((!is_symbol(car(carx))) || (!is_fxable(sc, cadr(carx)))))
	all_fx = false;
      if ((all_s7) &&
	  ((!is_pair(car(carx))) || (caar(carx) != sc->s7_let_symbol) ||
	   (!is_quoted_symbol(cadar(carx))) || (is_keyword(cadr(cadar(carx)))) ||
	   (!is_fxable(sc, cadr(carx)))))
	all_s7 = false;
    }
  if (!s7_is_proper_list(sc, cdr(code)))
    eval_error(sc, "stray dot in let-temporarily body: ~S", 37, cdr(code));

  if ((all_fx) || (all_s7))
    {
      pair_set_syntax_op(form, (all_fx) ? ((is_null(cdar(code))) ? OP_LET_TEMP_FX_1 : OP_LET_TEMP_FX) : OP_LET_TEMP_S7);
      for (x = car(code); is_pair(x); x = cdr(x))
	fx_annotate_arg(sc, cdar(x), sc->curlet);

      if ((optimize_op(form) == OP_LET_TEMP_FX_1) && (is_pair(cdr(code))) && (is_null(cddr(code))) && (is_fxable(sc, cadr(code))))
	{
	  fx_annotate_arg(sc, cdr(code), sc->curlet);
	  pair_set_syntax_op(form, OP_LET_TEMP_A_A);
	}
    }
  else
    {
      pair_set_syntax_op(form, OP_LET_TEMP_UNCHECKED);
      if ((is_pair(car(code))) && (is_null(cdar(code))) && (is_pair(caar(code))))
	{
	  s7_pointer var, val;
	  var = caar(code);
	  val = cadr(var);
	  var = car(var);
	  if ((is_pair(var)) && (car(var) == sc->setter_symbol) && (is_pair(cdr(var))) && (is_pair(cddr(var))) && (val == sc->F))
	    {
	      optimize_expression(sc, cadr(var), 0, sc->curlet, false);
	      optimize_expression(sc, caddr(var), 0, sc->curlet, false);
	      if ((is_fxable(sc, cadr(var))) && (is_fxable(sc, caddr(var))))
		{
		  fx_annotate_args(sc, cdr(var), sc->curlet);
		  pair_set_syntax_op(form, OP_LET_TEMP_SETTER);
		}}}}
}

static void op_let_temp_unchecked(s7_scheme *sc)
{
  sc->code = cdr(sc->code);
  sc->args = list_4(sc, car(sc->code), sc->nil, sc->nil, sc->nil);
  push_stack_direct(sc, OP_GC_PROTECT);
  /* sc->args: varlist, settees, old_values, new_values */
}

static bool op_let_temp_init1(s7_scheme *sc)
{
  while (is_pair(car(sc->args)))
    {
      /* eval car, add result to old-vals list, if any vars undefined, error */
      s7_pointer binding, settee, new_value;
      binding = caar(sc->args);
      settee = car(binding);
      new_value = cadr(binding);
      cadr(sc->args) = cons(sc, settee, cadr(sc->args));
      cadddr(sc->args) = cons(sc, new_value, cadddr(sc->args));
      car(sc->args) = cdar(sc->args);
      if (is_symbol(settee))                    /* get initial values */
	caddr(sc->args) = cons(sc, lookup_checked(sc, settee), caddr(sc->args));
      else
	{
	  if (is_pair(settee))
	    {
	      push_stack_direct(sc, OP_LET_TEMP_INIT1);
	      sc->code = settee;
	      return(true);
	    }
	  caddr(sc->args) = cons(sc, new_value, caddr(sc->args));
	}
    }
  car(sc->args) = cadr(sc->args);
  return(false);
}

typedef enum {goto_start, goto_begin, fall_through, goto_do_end_clauses, goto_safe_do_end_clauses,
	      goto_eval, goto_apply_lambda, goto_do_end, goto_top_no_pop, goto_apply,
	      goto_eval_args, goto_eval_args_top, goto_do_unchecked, goto_pop_read_list, goto_read_tok, goto_feed_to} goto_t;

static goto_t op_let_temp_init2(s7_scheme *sc)
{
  /* now eval set car new-val, cadr=settees, cadddr= new_values */
  while (is_pair(car(sc->args)))
    {
      s7_pointer settee, new_value, slot;
      settee = caar(sc->args);
      new_value = car(cadddr(sc->args));
      cadddr(sc->args) = cdr(cadddr(sc->args));
      car(sc->args) = cdar(sc->args);
      if ((!is_symbol(settee)) ||                /* (let-temporarily (((*s7* 'print-length) 32)) ...) */
	  (symbol_has_setter(settee)) ||         /*                  ((*features* #f))... */
	  (is_pair(new_value)))                  /*                  ((line-number (if (eq? caller top-level:) -1 line-number)))... */
	{
	  push_stack_direct(sc, OP_LET_TEMP_INIT2);
	  sc->code = list_3(sc, sc->set_symbol, settee, new_value);
	  return(goto_top_no_pop);
	}
      slot = symbol_to_slot(sc, settee);
      if (!is_slot(slot))
	unbound_variable_error(sc, settee);
      if (is_immutable_slot(slot))
	immutable_object_error(sc, set_elist_3(sc, immutable_error_string, sc->let_temporarily_symbol, settee));
      if (is_symbol(new_value))
	new_value = lookup_checked(sc, new_value);
      slot_set_value(slot, new_value);
    }
  car(sc->args) = cadr(sc->args);
  pop_stack(sc);
  /* push_stack_direct(sc, OP_LET_TEMP_DONE); */ /* we fall into LET_TEMP_DONE below so this seems redundant */
  sc->code = cdr(sc->code);
  if (is_pair(sc->code))
    {
      push_stack_direct(sc, OP_LET_TEMP_DONE);
      return(goto_begin);
    }
  sc->value = sc->nil; /* so (let-temporarily (<vars)) -> () like begin I guess */
  return(fall_through);
}

static bool op_let_temp_done1(s7_scheme *sc)
{
  while (is_pair(car(sc->args)))
    {
      s7_pointer settee;
      settee = caar(sc->args);
      sc->value = caaddr(sc->args);
      caddr(sc->args) = cdaddr(sc->args);
      car(sc->args) = cdar(sc->args);

      if ((is_pair(settee)) && (car(settee) == sc->s7_let_symbol) &&  /* (let-temporarily (((*s7* (symbol "print-length")) 43))...) */
	  ((is_keyword(cadr(settee))) ||
	   ((is_pair(cadr(settee))) && (caadr(settee) == sc->quote_symbol) && (is_symbol(cadadr(settee))))))
	{
	  s7_pointer sym;
	  sym = cadr(settee);
	  if (is_pair(sym)) sym = cadr(sym);
	  g_s7_let_set_fallback(sc, set_plist_3(sc, sc->s7_let, sym, sc->value));
	}
      else
	{
	  s7_pointer slot;
	  if ((!is_symbol(settee)) ||
	      (symbol_has_setter(settee)))                                   /* (let-temporarily ((x 1))...) -> (set! x 0) if x has a setter */
	    {
	      /* fprintf(stderr, "lt: (set! %s %s)\n", display(settee), display(sc->value)); */
	      /* TODO: handle setters, built-ins (openlets) */

	      push_stack_direct(sc, OP_LET_TEMP_DONE1);
	      if ((is_pair(sc->value)) || (is_symbol(sc->value)))            /* (let-temporarily ((*load-path* ())) 32) here: (set! *load-path* '(".")) */
		sc->code = list_3(sc, sc->set_symbol, settee, list_2(sc, sc->quote_symbol, sc->value));
	      else sc->code = list_3(sc, sc->set_symbol, settee, sc->value);
	      return(false); /* goto eval */
	    }
	  slot = symbol_to_slot(sc, settee);
	  if (is_immutable_slot(slot))
	    immutable_object_error(sc, set_elist_3(sc, immutable_error_string, sc->let_temporarily_symbol, settee));
	  slot_set_value(slot, sc->value);
	}
    }
  pop_stack(sc);
  sc->value = sc->code;
  if (is_multiple_value(sc->value))
    sc->value = splice_in_values(sc, multiple_value(sc->value));
  return(true);          /* goto start */
}

static void op_let_temp_s7(s7_scheme *sc) /* all entries are of the form ((*s7* 'field) fx-able-value) */
{
  s7_pointer p, var, field;
  s7_pointer *end;

  sc->code = cdr(sc->code);

  end = sc->stack_end;
  for (p = car(sc->code); is_pair(p); p = cdr(p))
    {
      s7_pointer old_value;
      var = caar(p);         /* var: (*s7* 'field) */
      field = cadadr(var);
      old_value = g_s7_let_ref_fallback(sc, set_plist_2(sc, sc->s7_let, field));
      push_stack(sc, OP_LET_TEMP_S7_UNWIND, old_value, field);
    }
  for (p = car(sc->code); is_pair(p); p = cdr(p), end += 4)
    {
      s7_pointer new_value;
      var = car(p);         /* var: ((*s7* 'field) vnew-val) */
      new_value = fx_call(sc, cdr(var));
      field = end[0];
      g_s7_let_set_fallback(sc, set_plist_3(sc, sc->s7_let, field, new_value));
    }
  sc->code = cdr(sc->code);
}

static void let_temp_done(s7_scheme *sc, s7_pointer args, s7_pointer code, s7_pointer let)
{
  /* called in call/cc, call-with-exit and, catch (unwind to catch) */
  push_stack_direct(sc, OP_EVAL_DONE);
  sc->args = T_Pos(args);
  sc->code = code;
  sc->curlet = let;
  eval(sc, OP_LET_TEMP_DONE);
}

static void let_temp_unwind(s7_scheme *sc, s7_pointer slot, s7_pointer new_value)
{
  if (slot_has_setter(slot)) /* setter has to be called because it might affect other vars (*clm-srate* -> mus-srate etc) */
    slot_set_value(slot, s7_apply_function(sc, slot_setter(slot), set_plist_2(sc, slot_symbol(slot), new_value)));
  else slot_set_value(slot, new_value);
}

static void op_let_temp_fx(s7_scheme *sc) /* all entries are of the form (symbol fx-able-value) */
{
  s7_pointer p, var, settee, new_val, slot;
  s7_pointer *end;

  sc->code = cdr(sc->code);

  end = sc->stack_end;
  for (p = car(sc->code); is_pair(p); p = cdr(p))
    {
      var = car(p);
      settee = car(var);
      slot = symbol_to_slot(sc, settee);
      if (!is_slot(slot))
	unbound_variable_error(sc, settee);
      if (is_immutable_slot(slot))
	immutable_object_error(sc, set_elist_3(sc, immutable_error_string, sc->let_temporarily_symbol, settee));
      push_stack(sc, OP_LET_TEMP_UNWIND, slot_value(slot), slot);
    }
  for (p = car(sc->code); is_pair(p); p = cdr(p), end += 4)
    {
      var = car(p);
      settee = car(var);
      new_val = fx_call(sc, cdr(var));
      slot = end[0];
      if (slot_has_setter(slot))
	slot_set_value(slot, s7_apply_function(sc, slot_setter(slot), set_plist_2(sc, settee, new_val)));
      else slot_set_value(slot, new_val);
    }
  sc->code = cdr(sc->code);
}

static void op_let_temp_fx_1(s7_scheme *sc) /* one entry */
{
  s7_pointer var, settee, new_val, slot;
  sc->code = cdr(sc->code);
  var = caar(sc->code);
  settee = car(var);
  slot = symbol_to_slot(sc, settee);
  if (!is_slot(slot))
    unbound_variable_error(sc, settee);
  if (is_immutable_slot(slot))
    immutable_object_error(sc, set_elist_3(sc, immutable_error_string, sc->let_temporarily_symbol, settee));
  push_stack(sc, OP_LET_TEMP_UNWIND, slot_value(slot), slot);
  new_val = fx_call(sc, cdr(var));
  if (slot_has_setter(slot))
    slot_set_value(slot, s7_apply_function(sc, slot_setter(slot), set_plist_2(sc, settee, new_val)));
  else slot_set_value(slot, new_val);
  sc->code = cdr(sc->code);
}

static s7_pointer fx_let_temp_a_a(s7_scheme *sc, s7_pointer code)
{
  s7_pointer result;
  op_let_temp_fx_1(sc);
  result = fx_call(sc, sc->code);
  pop_stack(sc);
  let_temp_unwind(sc, sc->code, sc->args);
  return(result);
}

static void op_let_temp_setter(s7_scheme *sc)
{
  s7_pointer var, slot, sym, e;
  sc->code = cdr(sc->code);
  var = caaar(sc->code);
  sym = fx_call(sc, cdr(var));
  e = sc->curlet;
  sc->curlet = fx_call(sc, cddr(var));
  slot = symbol_to_slot(sc, sym);
  sc->curlet = e;
  push_stack(sc, OP_LET_TEMP_SETTER_UNWIND, slot_setter(slot), slot);
  slot_set_setter(slot, sc->F);
  sc->code = cdr(sc->code);
}

static void op_let_temp_unwind(s7_scheme *sc)
{
  let_temp_unwind(sc, sc->code, sc->args);
  if (is_multiple_value(sc->value))
    sc->value = splice_in_values(sc, multiple_value(sc->value));
}

static void op_let_temp_s7_unwind(s7_scheme *sc)
{
  g_s7_let_set_fallback(sc, set_plist_3(sc, sc->s7_let, sc->code, sc->args));
  if (is_multiple_value(sc->value))
    sc->value = splice_in_values(sc, multiple_value(sc->value));
}

static void op_let_temp_setter_unwind(s7_scheme *sc)
{
  slot_set_setter(sc->code, sc->args);
  if (is_multiple_value(sc->value))
    sc->value = splice_in_values(sc, multiple_value(sc->value));
}


/* -------------------------------- quote -------------------------------- */
static inline s7_pointer check_quote(s7_scheme *sc, s7_pointer code)
{
  if (!is_pair(cdr(code)))                    /* (quote . -1) */
    {
      if (is_null(cdr(code)))
	eval_error(sc, "quote: not enough arguments: ~A", 31, code);
      eval_error(sc, "quote: stray dot?: ~A", 21, code);
    }
  if (is_not_null(cddr(code)))             /* (quote . (1 2)) or (quote 1 1) */
    eval_error(sc, "quote: too many arguments ~A", 28, code);

  pair_set_syntax_op(code, OP_QUOTE_UNCHECKED);
  return(cadr(code));

  /* I think a quoted list in another list can be applied to a function, come here and
   *   be changed to unchecked, set-cdr! or something clobbers the argument so we get
   *   here on the next time around with the equivalent of (quote . 0) if unchecked
   * so set-cdr! of constant -- if marked immutable, we could catch this case and clear.
   */
}


/* -------------------------------- and -------------------------------- */
static bool check_and(s7_scheme *sc, s7_pointer expr)
{
  /* this and check_or and check_if might not be called -- optimize_syntax can short-circuit it to return fx* choices */
  s7_pointer p, code;
  int32_t any_nils = 0, len;

  code = cdr(expr);
  if (is_null(code))
    {
      sc->value = sc->T;
      return(true);
    }
  for (len = 0, p = code; is_pair(p); p = cdr(p), len++)
    {
      s7_function callee;
      callee = fx_choose(sc, p, sc->curlet, let_symbol_is_safe);  /* c_callee can be nil! */
      if (!callee)
	any_nils++;
      set_c_call_checked(p, callee);
    }

  if (is_not_null(p))                                    /* (and . 1) (and #t . 1) */
    eval_error(sc, "and: stray dot?: ~A", 19, expr);

  if ((c_callee(code)) &&
      (is_proper_list_1(sc, cdr(code))))
    {
      if ((c_callee(code) == fx_is_pair_s) || (c_callee(code) == fx_is_pair_t))
	{
	  pair_set_syntax_op(expr, OP_AND_PAIR_P);
	  set_opt3_sym(expr, cadar(code));
	  set_opt2_con(expr, cadr(code));
	}
      else pair_set_syntax_op(expr, (any_nils > 0) ? OP_AND_AP : OP_AND_2);
    }
  else
    {
      pair_set_syntax_op(expr, (any_nils > 0) ? OP_AND_P : OP_AND_N);
      if ((any_nils == 1) && (len > 2))
	{
	  if (!has_fx(code))
	    pair_set_syntax_op(expr, OP_AND_SAFE_P1);
	  else
	    {
	      if (!has_fx(cdr(code)))
		pair_set_syntax_op(expr, OP_AND_SAFE_P2);
	      else
		{
		  if ((!has_fx(cddr(code))) && (len == 3))
		    pair_set_syntax_op(expr, OP_AND_SAFE_P3);
		}}}}
  return(false);
}

static bool op_and_pair_p(s7_scheme *sc)
{
  if (!is_pair(lookup(sc, opt3_sym(sc->code)))) /* cadadr(sc->code) */
    {
      sc->value = sc->F;
      return(true);
    }
  sc->code = opt2_con(sc->code);                /* caddr(sc->code); */
  return(false);
}

static bool op_and_ap(s7_scheme *sc)
{
  /* we know c_callee is set on sc->code, and there are only two branches */
  if (is_false(sc, fx_call(sc, cdr(sc->code))))
    {
      sc->value = sc->F;
      return(true);
    }
  sc->code = caddr(sc->code);
  return(false);
}

static void op_and_safe_p1(s7_scheme *sc) /* sc->code: (and (func...) (fx...)...) */
{
  sc->code = cdr(sc->code);
  push_stack_no_args_direct(sc, OP_AND_SAFE_P_REST);
  sc->code = car(sc->code);
}

static bool op_and_safe_p2(s7_scheme *sc)
{
  sc->value = fx_call(sc, cdr(sc->code));
  if (is_false(sc, sc->value)) return(true);
  sc->code = cddr(sc->code);
  push_stack_no_args_direct(sc, OP_AND_SAFE_P_REST);
  sc->code = car(sc->code);
  return(false);
}

static bool op_and_safe_p3(s7_scheme *sc)
{
  sc->value = fx_call(sc, cdr(sc->code));
  if (is_false(sc, sc->value)) return(true);
  sc->code = cddr(sc->code);
  sc->value = fx_call(sc, sc->code);
  if (is_false(sc, sc->value)) return(true);
  sc->code = cadr(sc->code);
  return(false);
}


/* -------------------------------- or -------------------------------- */
static bool check_or(s7_scheme *sc, s7_pointer expr)
{
  s7_pointer p, code;
  bool any_nils = false;

  code = cdr(expr);
  if (is_null(code))
    {
      sc->value = sc->F;
      return(true);
    }

  for (p = code; is_pair(p); p = cdr(p))
    {
      s7_function callee;
      callee = fx_choose(sc, p, sc->curlet, let_symbol_is_safe);
      if (!callee) any_nils = true;
      set_c_call_checked(p, callee);
    }
  if (is_not_null(p))
    eval_error(sc, "or: stray dot?: ~A", 18, expr);

  if ((c_callee(code)) &&
      (is_proper_list_1(sc, cdr(code)))) /* list_1 of cdr so there are 2 exprs */
    pair_set_syntax_op(expr, (any_nils) ? OP_OR_AP : OP_OR_2);
  else pair_set_syntax_op(expr, (any_nils) ? OP_OR_P : OP_OR_N);

  return(false);
}

static bool op_or_ap(s7_scheme *sc)
{
  /* we know c_callee is set on sc->code, and there are only two branches */
  sc->value = fx_call(sc, cdr(sc->code));
  if (is_true(sc, sc->value))
    return(true);
  sc->code = caddr(sc->code);
  return(false);
}


/* -------------------------------- if -------------------------------- */
#define choose_if_optc(Opc, One, Reversed, Not) ((One) ? ((Reversed) ? OP_ ## Opc ## _R : ((Not) ? OP_ ## Opc ## _N : OP_ ## Opc ## _P)) :  ((Not) ? OP_ ## Opc ## _N_N : OP_ ## Opc ## _P_P))

static void set_if_opts(s7_scheme *sc, s7_pointer form, bool one_branch, bool reversed) /* cdr(form) == sc->code */
{
  s7_pointer test, code;
  bool not_case = false;

  code = cdr(form);
  test = car(code);
  if ((!reversed) &&
      (is_pair(test)) &&
      (car(test) == sc->not_symbol))
    {
      if (!is_proper_list_1(sc, cdr(test))) return; /* (not) or (not a b) */
      not_case = true;
      test = cadr(test);
    }

  set_opt1_any(form, cadr(code));
  if (!one_branch) set_opt2_any(form, caddr(code));

  if (is_pair(test))
    {
      if (is_optimized(test))
	{
	  if (is_h_safe_c_d(test)) /* replace these with fx_and* */
	    {
	      pair_set_syntax_op(form, choose_if_optc(IF_A, one_branch, reversed, not_case));
	      if (not_case)
		set_c_call(cdar(code), fx_choose(sc, cdar(code), sc->curlet, let_symbol_is_safe));
	      else set_c_call(code, fx_choose(sc, code, sc->curlet, let_symbol_is_safe));
	      return;
	    }

	  if (is_h_safe_c_s(test))
	    {
	      uint8_t typ;
	      typ = symbol_type(car(test));
	      if (typ > 0)
		{
		  pair_set_syntax_op(form, choose_if_optc(IF_IS_TYPE_S, one_branch, reversed, not_case));
		  set_opt3_byte(code, typ);

		  if ((optimize_op(form) == OP_IF_IS_TYPE_S_P_P) &&
		      (is_fxable(sc, caddr(code))))
		    {
		      pair_set_syntax_op(form, OP_IF_IS_TYPE_S_P_A);
		      fx_annotate_arg(sc, cddr(code), sc->curlet);
		      set_opt2_any(form, cddr(code));
		    }
		}
	      else pair_set_syntax_op(form, choose_if_optc(IF_opSq, one_branch, reversed, not_case));
	      clear_has_fx(code);
	      set_opt2_sym(code, cadr(test));
	      return;
	    }
	  if (is_fxable(sc, test))
	    {
	      if (optimize_op(test) == OP_OR_2)
		{
		  pair_set_syntax_op(form, choose_if_optc(IF_OR2, one_branch, reversed, not_case));
		  set_opt2_pair(code, cdr(test));
		  set_opt3_pair(code, cddr(test));
		  return;
		}
	      if (optimize_op(test) == OP_AND_2)
		{
		  pair_set_syntax_op(form, choose_if_optc(IF_AND2, one_branch, reversed, not_case));
		  set_opt2_pair(code, cdr(test));
		  set_opt3_pair(code, cddr(test));
		  return;
		}
	      if (optimize_op(test) == OP_AND_3)
		{
		  pair_set_syntax_op(form, choose_if_optc(IF_AND3, one_branch, reversed, not_case));
		  set_opt2_pair(code, cdr(test));
		  set_opt3_pair(code, cddr(test));
		  set_opt1_pair(code, cdddr(test));
		  return;
		}

	      pair_set_syntax_op(form, choose_if_optc(IF_A, one_branch, reversed, not_case));
	      if (not_case)
		set_c_call(cdar(code), fx_choose(sc, cdar(code), sc->curlet, let_symbol_is_safe));
	      else set_c_call(code, fx_choose(sc, code, sc->curlet, let_symbol_is_safe));
	      if ((optimize_op(form) == OP_IF_A_P_P) &&
		  (is_fxable(sc, cadr(code))))
		{
		  pair_set_syntax_op(form, OP_IF_A_A_P);
		  fx_annotate_arg(sc, cdr(code), sc->curlet);
		  set_opt1_any(form, cdr(code));
		}
	    }
	  else
	    {
	      pair_set_syntax_op(form, choose_if_optc(IF_P, one_branch, reversed, not_case));
	      set_opt2_any(code, (one_branch) ? cadr(code) : cdr(code));
	      set_opt3_any(code, (not_case) ? cadar(code) : car(code));
	    }
	}
      else
	{
	  pair_set_syntax_op(form, choose_if_optc(IF_P, one_branch, reversed, not_case));
	  set_opt2_any(code, (one_branch) ? cadr(code) : cdr(code));
	  set_opt3_any(code, (not_case) ? cadar(code) : car(code));
	  if (is_syntactic_symbol(car(test)))
	    {
	      pair_set_syntax_op(test, symbol_syntax_op_checked(test));
	      if ((symbol_syntax_op(car(test)) == OP_AND) ||
		  (symbol_syntax_op(car(test)) == OP_OR))
		{
		  opcode_t new_op;
		  if (symbol_syntax_op(car(test)) == OP_AND)
		    check_and(sc, test);
		  else check_or(sc, test);
		  new_op = symbol_syntax_op_checked(test);
		  if ((new_op == OP_AND_P) || (new_op == OP_AND_AP) || (new_op == OP_AND_PAIR_P) ||
		      (new_op == OP_AND_N) || (new_op == OP_AND_SAFE_P1) || (new_op == OP_AND_SAFE_P2) || (new_op == OP_AND_SAFE_P3))
		    {
		      pair_set_syntax_op(form, choose_if_optc(IF_ANDP, one_branch, reversed, not_case));
		      set_opt2_any(code, (one_branch) ? cadr(code) : cdr(code));
		      set_opt3_pair(code, (not_case) ? cdadar(code) : cdar(code));
		    }
		  else
		    {
		      if ((new_op == OP_OR_P) || (new_op == OP_OR_AP))
			{
			  pair_set_syntax_op(form, choose_if_optc(IF_ORP, one_branch, reversed, not_case));
			  set_opt2_any(code, (one_branch) ? cadr(code) : cdr(code));
			  set_opt3_pair(code, (not_case) ? cdadar(code) : cdar(code));
			}
		    }}}}
    }
  else /* test is symbol or constant, but constant here is nutty */
    {
      if (is_safe_symbol(test))
	{
	  pair_set_syntax_op(form, choose_if_optc(IF_S, one_branch, reversed, not_case));
	  if ((optimize_op(form) == OP_IF_S_P_P) &&
	      (is_fxable(sc, caddr(code))))
	    {
	      pair_set_syntax_op(form, OP_IF_S_P_A);
	      fx_annotate_arg(sc, cddr(code), sc->curlet);
	      set_opt2_any(form, cddr(code));
	    }}}
}

/* (cond <> (else <>)) only happens in old-fashioned code, so set_if_opts covers if/when/unless but not cond */

static s7_pointer check_if(s7_scheme *sc)
{
  s7_pointer cdr_code, code, form;
  form = sc->code;
  code = cdr(form);

  if (!is_pair(code))                                /* (if) or (if . 1) */
    eval_error(sc, "(if): if needs at least 2 expressions: ~A", 41, form);

  cdr_code = cdr(code);
  if (!is_pair(cdr_code))                                /* (if 1) */
    eval_error(sc, "(if ~A): if needs another clause", 32, car(sc->code));

  if (is_pair(cdr(cdr_code)))
    {
      if (is_not_null(cddr(cdr_code)))                   /* (if 1 2 3 4) */
	eval_error(sc, "too many clauses for if: ~A", 27, form);
    }
  else
    {
      if (is_not_null(cdr(cdr_code)))                    /* (if 1 2 . 3) */
	eval_error(sc, "if: ~A has improper list?", 25, form);
    }
  pair_set_syntax_op(form, OP_IF_UNCHECKED);
  set_if_opts(sc, form, is_null(cdr(cdr_code)), false);
  return(code);
}

static void op_if(s7_scheme *sc)
{
  sc->code = check_if(sc);
  push_stack_no_args(sc, OP_IF1, cdr(sc->code));
  sc->code = car(sc->code);
}

static void op_if_unchecked(s7_scheme *sc)
{
  push_stack_no_args(sc, OP_IF1, cddr(sc->code));
  sc->code = cadr(sc->code);
}

static bool op_if1(s7_scheme *sc)
{
  if (is_true(sc, sc->value))
    sc->code = car(sc->code);
  else sc->code = unchecked_car(cdr(sc->code));   /* even pre-optimization, (if #f #f) ==> #<unspecified> because unique_car(sc->nil) = sc->unspecified */
  if (is_pair(sc->code))
    return(true);
  if (is_symbol(sc->code))
    sc->value = lookup_checked(sc, sc->code);
  else sc->value = sc->code;
  return(false);
}



/* -------------------------------- when -------------------------------- */
static void check_when(s7_scheme *sc)
{
  s7_pointer form, code;
  form = sc->code;
  code = cdr(sc->code);

  if (!is_pair(code))                                            /* (when) or (when . 1) */
    eval_error(sc, "when has no expression or body:  ~A", 35, form);
  if (!is_pair(cdr(code)))                                       /* (when 1) or (when 1 . 1) */
    eval_error(sc, "when has no body?:  ~A", 22, form);
  else
    {
      if (!s7_is_proper_list(sc, cddr(code)))
	eval_error(sc, "when: stray dot?", 16, form);
    }

  pair_set_syntax_op(form, OP_WHEN_P);
  if (is_null(cddr(code)))
    set_if_opts(sc, form, true, false);
  else
    {
      s7_pointer test;
      test = car(code);
      if (is_safe_symbol(test))
	{
	  pair_set_syntax_op(form, OP_WHEN_S);
	  set_opt2_con(form, cadr(code));
	  set_opt3_pair(form, cddr(code));
	}
      else
	{
	  /* fxable body doesn't happen very often -- a dozen or so hits in the standard tests */
	  if (is_fxable(sc, test))
	    {
	      pair_set_syntax_op(form, OP_WHEN_A);
	      if (is_pair(car(code))) set_opt2_pair(form, cdar(code));
	      set_opt3_pair(form, cdr(code));
	      set_c_call(code, fx_choose(sc, code, sc->curlet, let_symbol_is_safe)); /* "A" in when_a */
	      if (c_callee(code) == fx_and_2)
		pair_set_syntax_op(form, OP_WHEN_AND_2);
	      else
		{
		  if (c_callee(code) == fx_and_3)
		    pair_set_syntax_op(form, OP_WHEN_AND_3);
		}
	    }
	  else
	    {
	      if ((is_pair(test)) && (car(test) == sc->and_symbol))
		{
		  /* an experiment... */
		  opcode_t new_op;
		  pair_set_syntax_op(test, symbol_syntax_op_checked(test));
		  check_and(sc, test);
		  new_op = symbol_syntax_op_checked(test);
		  if (new_op == OP_AND_AP)
		    pair_set_syntax_op(form, OP_WHEN_AND_AP);
		}}}}
  push_stack_no_args(sc, OP_WHEN_PP, cdr(code));
  sc->code = car(code);
}

static bool op_when_s(s7_scheme *sc)
{
  if (is_true(sc, lookup(sc, cadr(sc->code))))
    {
      push_stack_no_args(sc, sc->begin_op, opt3_pair(sc->code)); /* cdddr(sc->code) */
      sc->code = opt2_con(sc->code);                             /* caddr(sc->code) */
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static bool op_when_a(s7_scheme *sc)
{
  if (is_true(sc, fx_call(sc, cdr(sc->code))))
    {
      push_stack_no_args(sc, sc->begin_op, cdr(opt3_pair(sc->code))); /* cdddr(sc->code) */
      sc->code = car(opt3_pair(sc->code));                            /* caddr(sc->code) */
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static bool op_when_and_2(s7_scheme *sc)
{
  if ((is_true(sc, fx_call(sc, opt2_pair(sc->code)))) && (is_true(sc, fx_call(sc, cdr(opt2_pair(sc->code))))))
    {
      push_stack_no_args(sc, sc->begin_op, cdr(opt3_pair(sc->code))); /* cdddr(sc->code) */
      sc->code = car(opt3_pair(sc->code));                            /* caddr(sc->code) */
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static bool op_when_and_3(s7_scheme *sc)
{
  if ((is_true(sc, fx_call(sc, opt2_pair(sc->code)))) && (is_true(sc, fx_call(sc, cdr(opt2_pair(sc->code))))) && (is_true(sc, fx_call(sc, cddr(opt2_pair(sc->code))))))
    {
      push_stack_no_args(sc, sc->begin_op, cdr(opt3_pair(sc->code))); /* cdddr(sc->code) */
      sc->code = car(opt3_pair(sc->code));                            /* caddr(sc->code) */
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static void op_when_p(s7_scheme *sc)
{
  push_stack_no_args(sc, OP_WHEN_PP, cddr(sc->code));
  sc->code = cadr(sc->code);
}

static bool op_when_and_ap(s7_scheme *sc)
{
  s7_pointer andp;
  andp = cdadr(sc->code);
  if (is_true(sc, fx_call(sc, andp)))
    {
      push_stack_no_args(sc, OP_WHEN_PP, cddr(sc->code));
      sc->code = cadr(andp);
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static bool op_when_pp(s7_scheme *sc)
{
  if (is_true(sc, sc->value))
    {
      if (is_pair(cdr(sc->code)))
	push_stack_no_args(sc, sc->begin_op, cdr(sc->code));
      sc->code = car(sc->code);
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}


/* -------------------------------- unless -------------------------------- */
static void check_unless(s7_scheme *sc)
{
  s7_pointer form, code;
  form = sc->code;
  code = cdr(sc->code);

  if (!is_pair(code))                                            /* (unless) or (unless . 1) */
    eval_error(sc, "unless has no expression or body:  ~A", 37, form);
  if (!is_pair(cdr(code)))                                       /* (unless 1) or (unless 1 . 1) */
    eval_error(sc, "unless has no body?:  ~A", 24, form);
  else
    {
      if (!s7_is_proper_list(sc, cddr(code)))
	eval_error(sc, "unless: stray dot?", 18, form);
    }

  pair_set_syntax_op(form, OP_UNLESS_P);
  if (is_null(cddr(code)))
    set_if_opts(sc, form, true, true);
  else
    {
      if (is_safe_symbol(car(code)))
	{
	  pair_set_syntax_op(form, OP_UNLESS_S);
	  set_opt2_con(form, cadr(code));
	  set_opt3_pair(form, cddr(code));
	}
      else
	{
	  if (is_fxable(sc, car(code)))
	    {
	      pair_set_syntax_op(form, OP_UNLESS_A);
	      set_opt2_con(form, cadr(code));
	      set_opt3_pair(form, cddr(code));
	      set_c_call(code, fx_choose(sc, code, sc->curlet, let_symbol_is_safe));
	    }
	}
    }
  push_stack_no_args(sc, OP_UNLESS_PP, cdr(code));
  sc->code = car(code);
}

static bool op_unless_s(s7_scheme *sc)
{
  if (is_false(sc, lookup(sc, cadr(sc->code))))
    {
      push_stack_no_args(sc, sc->begin_op, opt3_pair(sc->code)); /* cdddr(sc->code) */
      sc->code = opt2_con(sc->code);                             /* caddr(sc->code) */
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static bool op_unless_a(s7_scheme *sc)
{
  if (is_false(sc, fx_call(sc, cdr(sc->code))))
    {
      push_stack_no_args(sc, sc->begin_op, opt3_pair(sc->code)); /* cdddr(sc->code) */
      sc->code = opt2_con(sc->code);                             /* caddr(sc->code) */
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}

static void op_unless_p(s7_scheme *sc)
{
  push_stack_no_args(sc, OP_UNLESS_PP, cddr(sc->code));
  sc->code = cadr(sc->code);
}

static bool op_unless_pp(s7_scheme *sc)
{
  if (is_false(sc, sc->value))
    {
      if (is_pair(cdr(sc->code)))
	push_stack_no_args(sc, sc->begin_op, cdr(sc->code));
      sc->code = car(sc->code);
      return(false);
    }
  sc->value = sc->unspecified;
  return(true);
}


/* -------------------------------- begin -------------------------------- */
static bool op_begin(s7_scheme *sc, s7_pointer code)
{
  s7_pointer form;
  form = cdr(code);
  if (!s7_is_proper_list(sc, form))    /* proper list includes () */
    eval_error(sc, "unexpected dot? ~A", 18, code);
  if (is_null(form))                   /* (begin) -> () */
    {
      sc->value = sc->nil;
      return(true);
    }
  if (is_null(cdr(form)))
    pair_set_syntax_op(code, OP_BEGIN_1_UNCHECKED);
  else
    {
      if (is_null(cddr(form)))
	pair_set_syntax_op(code, OP_BEGIN_2_UNCHECKED);
      else pair_set_syntax_op(code, OP_BEGIN_UNCHECKED);
    }
  return(false);
}


/* -------------------------------- define -------------------------------- */
static s7_pointer print_truncate(s7_scheme *sc, s7_pointer code)
{
  if (tree_len(sc, code) > sc->print_length)
    return(object_to_truncated_string(sc, code, sc->print_length * 10));
  return(code);
}

static void check_define(s7_scheme *sc)
{
  s7_pointer func, caller, code;
  bool starred;

  code = cdr(sc->code);

  starred = (sc->cur_op == OP_DEFINE_STAR);
  if (starred)
    {
      caller = sc->define_star_symbol;
      sc->cur_op = OP_DEFINE_STAR_UNCHECKED;
    }
  else
    {
      if (sc->cur_op == OP_DEFINE)
	caller = sc->define_symbol;
      else caller = sc->define_constant_symbol;
    }

  if (!is_pair(code))
    eval_error_with_caller(sc, "~A: nothing to define? ~A", 25, caller, sc->code);     /* (define) */

  if (!is_pair(cdr(code)))
    {
      if (is_null(cdr(code)))
	eval_error_with_caller(sc, "~A: no value? ~A", 16, caller, sc->code);          /* (define var) */
      eval_error_with_caller(sc, "~A: stray dot? ~A", 17, caller, sc->code);           /* (define var . 1) */
    }
  if (!is_pair(car(code)))
    {
      if (is_not_null(cddr(code)))                                           /* (define var 1 . 2) */
	s7_error(sc, sc->syntax_error_symbol,
		 set_elist_3(sc, wrap_string(sc, "~A: more than one value? ~A", 27), caller, print_truncate(sc, sc->code)));
      if (starred)
	eval_error(sc, "define* is restricted to functions: (define* ~{~S~^ ~})", 55, sc->code);

      func = car(code);
      if (!is_symbol(func))                                                      /* (define 3 a) */
	eval_error_with_caller2(sc, "~A: can't define ~S, ~A (should be a symbol)", 44, caller, func, prepackaged_type_name(sc, func));
      if (is_keyword(func))                                                      /* (define :hi 1) */
	eval_error_with_caller(sc, "~A ~A: keywords are constants", 29, caller, func);
      if (is_syntactic_symbol(func))                                             /* (define and a) */
	{
	  if (sc->safety > NO_SAFETY)
	    s7_warn(sc, 128, "%s: syntactic keywords tend to behave badly if redefined\n", display(func));
	  set_local(func);
	}
      if ((is_pair(cadr(code))) &&               /* look for (define sym (lambda ...)) and treat it like (define (sym ...)...) */
	  ((caadr(code) == sc->lambda_symbol) ||
	   (caadr(code) == sc->lambda_star_symbol)) &&
	  (symbol_id(caadr(code)) == 0))
	{
	  /* not is_global here because that bit might not be set for initial symbols (why not? -- redef as method etc) */
	  if (!is_pair(cdadr(code)))                                         /* (define x (lambda . 1)) */
	    eval_error_with_caller(sc, "~A: stray dot? ~A", 17, caller, sc->code);
	  if (!is_pair(cddr(cadr(code))))                                    /* (define f (lambda (arg))) */
	    eval_error_with_caller(sc, "~A: no body: ~A", 15, caller, sc->code);
	  if (caadr(code) == sc->lambda_star_symbol)
	    check_lambda_star_args(sc, cadadr(code), cddr(cadr(code)));
	  else check_lambda_args(sc, cadadr(code), NULL);
	  optimize_lambda(sc, caadr(code) == sc->lambda_symbol, func, cadadr(code), cddr(cadr(code)));
	}
    }
  else
    {
      func = caar(code);
      if (!is_symbol(func))                                                      /* (define (3 a) a) */
	eval_error_with_caller2(sc, "~A: can't define ~S, ~A (should be a symbol)", 44, caller, func, prepackaged_type_name(sc, func));
      if (is_syntactic_symbol(func))                                             /* (define (and a) a) */
	{
	  if (sc->safety > NO_SAFETY)
	    s7_warn(sc, 128, "%s: syntactic keywords tend to behave badly if redefined\n", display(func));
	  set_local(func);
	}
      if (starred)
	set_cdar(code, check_lambda_star_args(sc, cdar(code), cdr(code)));
      else check_lambda_args(sc, cdar(code), NULL);
      optimize_lambda(sc, !starred, func, cdar(code), cdr(code));
    }

  if ((sc->cur_op == OP_DEFINE) ||
      (sc->cur_op == OP_DEFINE_CONSTANT)) /* ?? 10-May-18 */
    {
      if ((is_pair(car(code))) &&
	  (!symbol_has_setter(func)) &&
	  (!is_possibly_constant(func)))
	pair_set_syntax_op(sc->code, OP_DEFINE_FUNCHECKED);
      else pair_set_syntax_op(sc->code, OP_DEFINE_UNCHECKED);
    }
  else
    {
      if (starred)
	pair_set_syntax_op(sc->code, OP_DEFINE_STAR_UNCHECKED);
      else pair_set_syntax_op(sc->code, OP_DEFINE_CONSTANT_UNCHECKED);
    }
}

static bool op_define_unchecked(s7_scheme *sc)
{
  s7_pointer code, locp;
  code = cdr(sc->code);

  if ((is_pair(car(code))) && (has_location(car(code))))
    locp = car(code);
  else
    {
      for (locp = cdr(code); is_pair(locp); locp = cdr(locp))
	if ((is_pair(car(locp))) && (has_location(car(locp))))
	  {
	    locp = car(locp);
	    break;
	  }
      /* if (is_null(locp)) fprintf(stderr, "perhaps %s -> %d\n", display_80(sc->code), port_line_number(sc->input_port)); */
    }

  if ((sc->cur_op == OP_DEFINE_STAR_UNCHECKED) && /* sc->cur_op changed above if define* */
      (is_pair(cdar(code))))
    {
      sc->value = make_closure(sc, cdar(code), cdr(code), T_CLOSURE_STAR, CLOSURE_ARITY_NOT_SET);
      /* closure_body may not be cdr(code) after make_closure (add_trace) */
      if ((is_pair(locp)) && (has_location(locp)))
	{
	  pair_set_location(closure_body(sc->value), pair_location(locp));
	  set_has_location(closure_body(sc->value));
	}
      sc->code = caar(code);
      return(false);
    }

  if (!is_pair(car(code)))
    {
      s7_pointer x;
      x = car(code);
      sc->code = cadr(code);
      if (is_pair(sc->code))
	{
	  push_stack(sc, OP_DEFINE1, sc->nil, x);
	  sc->cur_op = optimize_op(sc->code);
	  return(true);
	}
      if (is_symbol(sc->code))
	sc->value = lookup_global(sc, sc->code);
      else sc->value = sc->code;
      sc->code = x;
    }
  else
    {
      s7_pointer x, args;
      /* a closure.  If we called this same code earlier (a local define), the only thing
       *   that is new here is the environment -- we can't blithely save the closure object
       *   in opt2 somewhere, and pick it up the next time around (since call/cc might take
       *   us back to the previous case).  We also can't re-use opt2(sc->code) because opt2
       *   is not cleared in the gc.
       */
      args = cdar(code);
      x = make_closure(sc, args, cdr(code), T_CLOSURE | T_COPY_ARGS, (is_null(args)) ? 0 : CLOSURE_ARITY_NOT_SET);
      if ((is_pair(locp)) && (has_location(locp)))
	{
	  pair_set_location(closure_body(x), pair_location(locp));
	  set_has_location(closure_body(x));
	}
      sc->value = T_Pos(x);
      sc->code = caar(code);
    }
  return(false);
}

static s7_pointer make_funclet(s7_scheme *sc, s7_pointer new_func, s7_pointer func_name, s7_pointer outer_let)
{
  s7_pointer new_let, arg;
  /* fprintf(stderr, "%s[%d]: %s %s %s\n", __func__, __LINE__, display(new_func), display(func_name), display(outer_let)); */
  new_cell_no_check(sc, new_let, T_LET | T_FUNCLET);
  let_set_id(new_let, ++sc->let_number);
  let_set_outlet(new_let, outer_let);
  closure_set_let(new_func, new_let);
  funclet_set_function(new_let, func_name); /* __func__ returns at least funclet_function */
  let_set_slots(new_let, slot_end(sc));

  arg = closure_args(new_func);
  if (is_null(arg))
    {
      let_set_slots(new_let, slot_end(sc));
      return(new_let);
    }

  if (is_safe_closure(new_func))
    {
      s7_pointer last_slot = NULL;
      if (is_closure(new_func))
	{
	  if (is_pair(arg))
	    {
	      last_slot = make_slot(sc, car(arg), sc->nil);
	      slot_set_next(last_slot, slot_end(sc));
	      let_set_slots(new_let, last_slot);
	      symbol_set_local_slot(car(arg), let_id(new_let), last_slot);
	      for (arg = cdr(arg); is_pair(arg); arg = cdr(arg))
		last_slot = add_slot_at_end(sc, let_id(new_let), last_slot, car(arg), sc->nil);
	    }
	  if (is_symbol(arg))
	    {
	      if (last_slot)
		last_slot = add_slot_at_end(sc, let_id(new_let), last_slot, arg, sc->nil);
	      else
		{
		  last_slot = make_slot(sc, arg, sc->nil);
		  slot_set_next(last_slot, slot_end(sc));
		  let_set_slots(new_let, last_slot);
		  symbol_set_local_slot(arg, let_id(new_let), last_slot);
		}
	      set_is_rest_slot(last_slot);
	    }
	  /* slot_set_next(last_slot, slot_end(sc)); */
	}
      else
	{
	  s7_pointer slot, first_default;
	  first_default = sc->nil;
	  let_set_slots(new_let, slot_end(sc));
	  for (; is_pair(arg); arg = cdr(arg))
	    {
	      s7_pointer par;
	      par = car(arg);
	      if (is_pair(par))
		{
		  s7_pointer val;
		  val = cadr(par);
		  slot = make_slot_2(sc, new_let, car(par), sc->nil);
		  slot_set_expression(slot, val);
		  if ((is_symbol(val)) || (is_pair(val)))
		    {
		      if (is_null(first_default))
			first_default = slot;
		      set_slot_defaults(slot);
		    }
		}
	      else
		{
		  if (is_keyword(par))
		    {
		      if (par == sc->key_rest_symbol)
			{
			  arg = cdr(arg);
			  slot = make_slot_2(sc, new_let, car(arg), sc->nil);
			  slot_set_expression(slot, sc->nil);
			}
		    }
		  else
		    {
		      slot = make_slot_2(sc, new_let, par, sc->nil);
		      slot_set_expression(slot, sc->F);
		    }
		}
	    }
	  if (is_symbol(arg))
	    {
	      slot = make_slot_2(sc, new_let, arg, sc->nil);     /* set up rest arg */
	      set_is_rest_slot(slot);
	      slot_set_expression(slot, sc->nil);
	    }
	  if (tis_slot(let_slots(new_let)))
	    {
	      let_set_slots(new_let, reverse_slots(sc, let_slots(new_let)));
	      slot_set_pending_value(let_slots(new_let), first_default);
	    }
	}
      set_immutable_let(new_let);
    }
  else let_set_slots(new_let, slot_end(sc)); /* if unsafe closure, arg-holding-let will be created on each call */
  return(new_let);
}

static bool op_define_constant(s7_scheme *sc)
{
  s7_pointer code;
  code = cdr(sc->code);
  if ((!is_pair(code)) || (!is_pair(cdr(code)))) /* (define-constant) */
    eval_error(sc, "define-constant: not enough arguments: ~S", 41, sc->code);

  if (is_keyword(car(code)))                   /* (define-constant :rest :allow-other-keys) */
    {
      if (car(code) == cadr(code))             /* (define-constant pi pi) returns pi */
	{
	  sc->value = car(code);
	  return(true);
	}
      eval_error_with_caller(sc, "~A ~A: keywords are constants", 29, sc->define_constant_symbol, car(code));
    }

  if ((is_symbol(car(code))) &&                /* (define-constant abs abs): "abs will not be touched" */
      (car(code) == cadr(code)) &&
      (symbol_id(car(code)) == 0) &&           /* else (let iter ... (define-constant iter iter) ...) -> segfault on later calls */
      (is_null(cddr(code))))
    {
      s7_pointer sym;
      sym = car(code);
      set_immutable(global_slot(sym)); /* id == 0 so its global */
      set_possibly_constant(sym);
      sc->value = lookup_checked(sc, car(code));
      return(true);
    }
  push_stack_no_args(sc, OP_DEFINE_CONSTANT1, car(code));
  return(false);
}

static void op_define_constant1(s7_scheme *sc)
{
  if (is_pair(sc->code))
    sc->code = car(sc->code); /* (define-constant (ex3 a)...) */
  if (is_symbol(sc->code))
    {
      s7_pointer slot;
      slot = symbol_to_slot(sc, sc->code);
      set_possibly_constant(sc->code);
      set_immutable(slot);
      if (is_any_closure(slot_value(slot)))
	set_immutable(slot_value(slot)); /* for the optimizer mainly */
    }
}

static inline void define_funchecked(s7_scheme *sc)
{
  s7_pointer new_func, code;
  /* fprintf(stderr, "%s[%d]: %s\n", __func__, __LINE__, display(sc->code)); */
  code = cdr(sc->code);
  sc->value = caar(code); /* func name */

  new_cell(sc, new_func, T_CLOSURE | T_COPY_ARGS);
  closure_set_args(new_func, cdar(code));
  closure_set_body(new_func, cdr(code));
  if (is_pair(cddr(code))) set_closure_has_multiform(new_func); else set_closure_has_one_form(new_func);
  closure_set_setter(new_func, sc->F);
  closure_set_arity(new_func, CLOSURE_ARITY_NOT_SET);
  sc->capture_let_counter++;

  if (is_safe_closure_body(cdr(code)))
    {
      set_safe_closure(new_func);
      if (is_very_safe_closure_body(cdr(code)))
	set_very_safe_closure(new_func);
      make_funclet(sc, new_func, sc->value, sc->curlet);
    }
  else closure_set_let(new_func, sc->curlet);  /* unsafe closures created by other functions do not support __func__ */

  if (let_id(sc->curlet) < symbol_id(sc->value))
    sc->let_number++; /* dummy let, force symbol lookup */
  another_slot(sc, sc->curlet, sc->value, new_func, sc->let_number);
  sc->value = new_func;
}

static s7_pointer cur_op_to_caller(s7_scheme *sc, opcode_t op)
{
  switch (op)
    {
    case OP_DEFINE_MACRO:          return(sc->define_macro_symbol);
    case OP_DEFINE_MACRO_STAR:     return(sc->define_macro_star_symbol);
    case OP_DEFINE_BACRO:          return(sc->define_bacro_symbol);
    case OP_DEFINE_BACRO_STAR:     return(sc->define_bacro_star_symbol);
    case OP_DEFINE_EXPANSION:      return(sc->define_expansion_symbol);
    case OP_DEFINE_EXPANSION_STAR: return(sc->define_expansion_star_symbol);
    case OP_MACRO:                 return(sc->macro_symbol);
    case OP_MACRO_STAR:            return(sc->macro_star_symbol);
    case OP_BACRO:                 return(sc->bacro_symbol);
    case OP_BACRO_STAR:            return(sc->bacro_star_symbol);
#if S7_DEBUGGING
    default:
      fprintf(stderr, "%s[%d]: got %s?\n", __func__, __LINE__, op_names[op]);
#endif
    }
  return(sc->define_macro_symbol);
}

static s7_pointer check_define_macro(s7_scheme *sc, opcode_t op)
{
  s7_pointer mac_name, args, caller;
  caller = cur_op_to_caller(sc, op);

  if (!is_pair(sc->code))                                               /* (define-macro . 1) */
    eval_error_with_caller(sc, "~A name missing (stray dot?): ~A", 32, caller, sc->code);
  if (!is_pair(car(sc->code)))                                      /* (define-macro a ...) */
    return(wrong_type_argument_with_type(sc, caller, 1, car(sc->code), wrap_string(sc, "a list: (name ...)", 18)));

  mac_name = caar(sc->code);
  if (!is_symbol(mac_name))
    eval_error_with_caller(sc, "~A: ~S is not a symbol?", 23, caller, mac_name);
  if (is_syntactic_symbol(mac_name))
    {
      if (sc->safety > NO_SAFETY)
	s7_warn(sc, 128, "%s: syntactic keywords tend to behave badly if redefined\n", display(mac_name));
      set_local(mac_name);
    }
  if (is_constant_symbol(sc, mac_name))
    eval_error_with_caller(sc, "~A: ~S is constant", 18, caller, mac_name);

  if (!is_pair(cdr(sc->code)))                                        /* (define-macro (...)) */
    eval_error_with_caller(sc, "~A ~A, but no body?", 19, caller, mac_name);

  args = cdar(sc->code);
  if ((!is_list(args)) &&
      (!is_symbol(args)))
    return(s7_error(sc, sc->syntax_error_symbol,                      /* (define-macro (mac . 1) ...) */
		    set_elist_3(sc, wrap_string(sc, "macro ~A argument list is ~S?", 29), mac_name, args)));

  if ((op == OP_DEFINE_MACRO) || (op == OP_DEFINE_BACRO) || (op == OP_DEFINE_EXPANSION))
    {
      for ( ; is_pair(args); args = cdr(args))
	if (!is_symbol(car(args)))
	  return(s7_error(sc, sc->syntax_error_symbol,                    /* (define-macro (mac 1) ...) */
			  set_elist_3(sc, wrap_string(sc, "~A parameter name, ~A, is not a symbol", 38), caller, car(args))));
      check_lambda_args(sc, cdar(sc->code), NULL);
    }
  else set_cdar(sc->code, check_lambda_star_args(sc, cdar(sc->code), NULL));
  return(sc->code);
}

static s7_pointer check_macro(s7_scheme *sc, opcode_t op)
{
  s7_pointer args, caller;
  caller = cur_op_to_caller(sc, op);

  if (!is_pair(sc->code))                                               /* (define-macro . 1) */
    eval_error_with_caller(sc, "~A name missing (stray dot?): ~A", 32, caller, sc->code);
  if (!is_pair(cdr(sc->code)))                                        /* (define-macro (...)) */
    eval_error_with_caller(sc, "(~A ~A) has no body?", 20, caller, car(sc->code));

  args = car(sc->code);
  if ((!is_list(args)) &&
      (!is_symbol(args)))
    return(s7_error(sc, sc->syntax_error_symbol,                      /* (define-macro (mac . 1) ...) */
		    set_elist_2(sc, wrap_string(sc, "macro argument list is ~S?", 26), args)));

  if ((op == OP_MACRO) || (op == OP_BACRO))
    {
      for ( ; is_pair(args); args = cdr(args))
	if (!is_symbol(car(args)))
	  return(s7_error(sc, sc->syntax_error_symbol,                    /* (define-macro (mac 1) ...) */
			  set_elist_3(sc, wrap_string(sc, "~A parameter name, ~A, is not a symbol", 38), caller, car(args))));
      check_lambda_args(sc, car(sc->code), NULL);
    }
  else set_car(sc->code, check_lambda_star_args(sc, car(sc->code), NULL));
  return(sc->code);
}

static s7_pointer op_define_macro_with_setter(s7_scheme *sc)
{
  sc->code = sc->value;
  if ((!is_pair(sc->code)) ||
      (!is_pair(car(sc->code))) ||
      (!is_symbol(caar(sc->code))))
    return(eval_error(sc, "define-macro: ~S does not look like a macro?", 44, sc->code)); /* return here for callgrind */
  if ((is_immutable(sc->curlet)) &&
      (is_let(sc->curlet))) /* not () */
    eval_error(sc, "define-macro ~S: let is immutable", 33, caar(sc->code));
  sc->value = make_macro(sc, sc->cur_op, false);
  return(NULL);
}

static bool op_define_macro(s7_scheme *sc)
{
  sc->code = cdr(sc->code);
  check_define_macro(sc, sc->cur_op);
  if (symbol_has_setter(caar(sc->code)))
    {
      s7_pointer x;
      x = symbol_to_slot(sc, caar(sc->code));
      if ((is_slot(x)) &&
	  (slot_has_setter(x)))
	{
	  sc->value = bind_symbol_with_setter(sc, OP_DEFINE_MACRO_WITH_SETTER, caar(sc->code), sc->code);
	  if (sc->value == sc->no_value)
	    return(false);
	  sc->code = sc->value;
	}
    }
  if ((is_immutable(sc->curlet)) &&
      (is_let(sc->curlet))) /* not () */
    eval_error(sc, "define-macro ~S: let is immutable", 33, caar(sc->code)); /* need eval_error_any_with_caller? */
  sc->value = make_macro(sc, sc->cur_op, false);
  return(true);
}

static bool op_macro(s7_scheme *sc) /* (macro (x) `(+ ,x 1)) */ /* TODO: OP_MACRO_UNCHECKED? */
{
  sc->code = cdr(sc->code);
  check_macro(sc, sc->cur_op);
  sc->value = make_macro(sc, sc->cur_op, true);
  return(true);
}

static bool op_macro_d(s7_scheme *sc)
{
  sc->value = lookup(sc, car(sc->code));
  if (!is_macro(sc->value))   /* for-each (etc) called a macro before, now it's something else -- a very rare case */
    {
      set_unsafe_optimize_op(sc->code, OP_PAIR_SYM); /* or op_unknown* based on args? */
      return(true);
    }
  sc->args = copy_proper_list(sc, cdr(sc->code));
  sc->code = sc->value;                       /* the macro */
  push_stack_op_let(sc, OP_EVAL_MACRO);
  sc->curlet = make_let(sc, closure_let(sc->code));
  return(false);
}

static void apply_macro_star_1(s7_scheme *sc);

static bool op_macro_star_d(s7_scheme *sc)
{
  sc->value = lookup(sc, car(sc->code));
  if (!is_macro_star(sc->value))
    {
      set_unsafe_optimize_op(sc->code, OP_PAIR_SYM);
      return(true);
    }
  sc->args = copy_proper_list(sc, cdr(sc->code));
  sc->code = sc->value;
  push_stack_op_let(sc, OP_EVAL_MACRO);
  sc->curlet = make_let(sc, closure_let(sc->code));
  apply_macro_star_1(sc);
  return(false);
}

static void transfer_macro_info(s7_scheme *sc, s7_pointer mac)
{
  s7_pointer body;
  body = closure_body(mac);
  if (has_pair_macro(mac))
    {
      set_maclet(sc->curlet);
      funclet_set_function(sc->curlet, pair_macro(body));
    }
  if (has_location(body))
    {
      let_set_file(sc->curlet, pair_file_number(body));
      let_set_line(sc->curlet, pair_line_number(body));
      set_has_let_file(sc->curlet);
    }
}

