  v1 = lookup(sc, car(args));
  args = cdr(args);
  v2 = lookup(sc, car(args));
  v3 = lookup(sc, cadr(args));

  sc->code = opt1_lambda(sc->code);
  sc->curlet = make_let(sc, closure_let(sc->code));
  id = let_id(sc->curlet);

  p = closure_args(sc->code);
