static void op_safe_closure_all_s(s7_scheme *sc)
{
  s7_pointer args, let, x;
  uint64_t id;

  args = cdr(sc->code);
  sc->code = opt1_lambda(sc->code);
  id = ++sc->let_number;
  let = closure_let(sc->code);
  let_set_id(let, id);

  for (x = let_slots(let); tis_slot(x); x = next_slot(x), args = cdr(args))
    {
      slot_set_value(x, lookup(sc, car(args)));
      symbol_set_local_slot(slot_symbol(x), id, x);
    }

  sc->curlet = let;
  sc->code = closure_body(sc->code);
  if (is_pair(cdr(sc->code)))
    push_stack_no_args(sc, sc->begin_op, cdr(sc->code));
  sc->code = car(sc->code);
}

static void op_safe_closure_fx(s7_scheme *sc)
{
  s7_pointer args, p, let, x, z;
  uint64_t id;

  sc->args = safe_list_if_possible(sc, integer(opt3_arglen(sc->code)));
  for (args = cdr(sc->code), p = sc->args; is_pair(args); args = cdr(args), p = cdr(p))
    set_car(p, fx_call(sc, args));
  sc->code = opt1_lambda(sc->code);

  id = ++sc->let_number;
  let = closure_let(sc->code);
  let_set_id(let, id);

  for (x = let_slots(let), z = sc->args; tis_slot(x); x = next_slot(x), z = cdr(z))
    {
      slot_set_value(x, car(z));
      symbol_set_local_slot(slot_symbol(x), id, x);
    }

  clear_list_in_use(sc->args);
  sc->args = sc->nil;

  sc->curlet = let;
  sc->code = closure_body(sc->code);
  if (is_pair(cdr(sc->code)))
    push_stack_no_args(sc, sc->begin_op, cdr(sc->code));
  sc->code = car(sc->code);
}

static inline void op_closure_all_s(s7_scheme *sc)
{
  s7_pointer args, p, e, last_slot;
  s7_int id;
  /* in this case, we have just lambda (not lambda*), and no dotted arglist,
   *   and no accessed symbols in the arglist, and we know the arglist matches the parameter list.
   */
  args = cdr(sc->code);
  sc->code = opt1_lambda(sc->code);
  e = make_let(sc, closure_let(sc->code));
  sc->z = e;
  id = let_id(e);

  p = closure_args(sc->code);
  add_slot(sc, e, car(p), lookup(sc, car(args)));
  last_slot = let_slots(e);
  for (p = cdr(p), args = cdr(args); is_pair(p); p = cdr(p), args = cdr(args))
    last_slot = add_slot_at_end(sc, id, last_slot, car(p), lookup(sc, car(args))); /* main such call in lt (fx_s is 1/2, this is 1/5 of all calls) */

  sc->curlet = e;
  sc->z = sc->nil;
  sc->code = T_Pair(closure_body(sc->code));
  if (is_pair(cdr(sc->code)))
    {
      check_stack_size(sc);
      push_stack_no_args(sc, sc->begin_op, cdr(sc->code));
    }
  sc->code = car(sc->code);
}

static void just_another_slot(s7_scheme *sc, s7_pointer let, s7_pointer symbol)
{
  s7_pointer slot;
  new_cell(sc, slot, T_SLOT);
  slot_set_symbol(slot, symbol);
  slot_set_value(slot, sc->F); /* needed if GC runs before true value is set */
  slot_set_next(slot, let_slots(let));
  let_set_slots(let, slot);
}

static s7_pointer just_add_slot_at_end(s7_scheme *sc, s7_pointer last_slot, s7_pointer symbol)
{
  s7_pointer slot;
  new_cell(sc, slot, T_SLOT);
  slot_set_symbol(slot, symbol);
  slot_set_value(slot, sc->F);
  slot_set_next(slot, slot_end(sc));
  slot_set_next(last_slot, slot);
  return(slot);
}

/* op_closure_any_fx could also be done this way, but it's not called very often */

static void op_closure_fx(s7_scheme *sc)
{
  s7_pointer e, exprs, pars, func, slot, last_slot;
  s7_int id;

  exprs = cdr(sc->code);
  /* sc->w = exprs; */              /* unnecessary? GC protection, but sc->w can be clobbered by fx_call, sc->code should provide the protection here */
  func = opt1_lambda(sc->code);
  e = make_let(sc, closure_let(func));
  sc->z = e;

  pars = closure_args(func);
  just_another_slot(sc, e, car(pars));
  /* this and below are wrong because they set local_slot -- need just_another_slot and just_add_slot_at_end -- no id, no value, no local_slot */
  last_slot = let_slots(e);
  slot_set_pending_value(last_slot, fx_call(sc, exprs));

  for (pars = cdr(pars), exprs = cdr(exprs); is_pair(pars); pars = cdr(pars), exprs = cdr(exprs))
    {
      last_slot = just_add_slot_at_end(sc, last_slot, car(pars));
      slot_set_pending_value(last_slot, fx_call(sc, exprs));
      /* maybe reset sc->z=e? or push e on stack? */
    }
  sc->curlet = e;
  sc->z = sc->nil;
  id = let_id(e);
  for (slot = let_slots(e); tis_slot(slot); slot = next_slot(slot))
    {
      slot_set_value(slot, slot_pending_value(slot));
      symbol_set_id(slot_symbol(slot), id);
      symbol_set_local_slot(slot_symbol(slot), id, slot);
      set_local(slot_symbol(slot));
    }
  sc->code = T_Pair(closure_body(func));

  if (is_pair(cdr(sc->code)))
    {
      check_stack_size(sc);
      push_stack_no_args(sc, sc->begin_op, cdr(sc->code));
    }
  sc->code = car(sc->code);
}

static bool check_closure_any(s7_scheme *sc)
{
  /* can't use closure_is_fine -- (lambda args 1) and (lambda (name . args) 1) are both arity -1 for the internal arity checkers! */
  if ((symbol_ctr(car(sc->code)) != 1) ||
      (unchecked_slot_value(local_slot(car(sc->code))) != opt1_lambda_unchecked(sc->code)))
    {
      s7_pointer f;
      f = lookup_unexamined(sc, car(sc->code));
      if ((f != opt1_lambda_unchecked(sc->code)) &&
	  ((!f) ||
	   ((typesflag(f) & (TYPE_MASK | T_SAFE_CLOSURE)) != T_CLOSURE) ||
	   (!is_symbol(closure_args(f)))))
	{
	  sc->last_function = f;
	  return(false);
	}
      set_opt1_lambda(sc->code, f);
    }
  return(true);
}

static void op_closure_any_fx(s7_scheme *sc) /* for (lambda a ...) ? */
{
  s7_pointer p, old_args;
  sc->w = cdr(sc->code);               /* args aren't evaluated yet */
  sc->args = make_list(sc, integer(opt3_arglen(sc->code)), sc->F);
  for (p = sc->args, old_args = sc->w; is_pair(p); p = cdr(p), old_args = cdr(old_args))
    set_car(p, fx_call(sc, old_args));
  sc->w = sc->nil;
  p = opt1_lambda(sc->code);
  sc->curlet = make_let_with_slot(sc, closure_let(p), closure_args(p), sc->args);
  sc->code = T_Pair(closure_body(p));
}

