static void op_tc_let_when_laa(s7_scheme *sc, bool when, s7_pointer code)
{
  s7_pointer p, body, if_test, if_true, la, let_slot, laa, let_var, outer_let, inner_let;
  let_var = caadr(code);
  body = caddr(code);
  outer_let = sc->curlet;
  sc->curlet = make_let_with_slot(sc, sc->curlet, car(let_var), fx_call(sc, cdr(let_var)));
  inner_let = sc->curlet;
  push_stack_no_let_no_code(sc, OP_GC_PROTECT, inner_let);
  let_slot = let_slots(sc->curlet);
  let_var = cdr(let_var);

  if_test = cdr(body);
  if_true = cddr(body);
  for (p = if_true; is_pair(cdr(p)); p = cdr(p));
  la = cdar(p);
  laa = cddar(p);

  if ((car(la) == slot_symbol(let_slots(outer_let))) &&
      (car(laa) == slot_symbol(next_slot(let_slots(outer_let)))))
    {
      if ((cdr(if_true) == p) && (!when))
	{
	  s7_pointer a1, a2;
	  a1 = slot_value(let_slots(outer_let));
	  a2 = slot_value(next_slot(let_slots(outer_let)));
	  if ((is_input_port(a1)) && (is_output_port(a2)) && (is_string_port(a1)) && (is_file_port(a2)) &&
	      (!port_is_closed(a1)) && (!port_is_closed(a2)) && (c_callee(if_true) == fx_c_tU_direct) &&
	      (c_callee(let_var) == fx_c_t_direct) && (((s7_p_pp_t)opt3_direct(cdar(if_true))) == write_char_p_pp) &&
	      (((s7_p_p_t)opt2_direct(cdar(let_var))) == read_char_p_p) && (c_callee(if_test) == fx_is_eof_t))
	    {
	      int32_t c;
	      a1 = slot_value(let_slots(outer_let));
	      a2 = slot_value(next_slot(let_slots(outer_let)));
	      c = (int32_t)s7_character(slot_value(let_slots(inner_let)));
	      while (c != EOF)
		{
		  file_write_char(sc, (uint8_t)c, a2);
		  c = string_read_char(sc, a1);
		}
	    }
	  else
	    {
	      while (fx_call(sc, if_test) == sc->F)
		{
		  fx_call(sc, if_true);
		  sc->curlet = outer_let;
		  slot_set_value(let_slot, fx_call(sc, let_var));
		  sc->curlet = inner_let;
		}
	    }
	}
      else
	{
	  while (true)
	    {
	      p = fx_call(sc, if_test);
	      if (when) {if (p == sc->F) break;} else {if (p != sc->F) break;}
	      for (p = if_true; is_pair(cdr(p)); p = cdr(p))
		fx_call(sc, p);
	      sc->curlet = outer_let;
	      slot_set_value(let_slot, fx_call(sc, let_var));
	      sc->curlet = inner_let;
	    }
	}
    }
  else
    {
      s7_pointer la_slot, laa_slot;
      la_slot = let_slots(let_outlet(sc->curlet));
      laa_slot = next_slot(la_slot);
      while (true)
	{
	  p = fx_call(sc, if_test);
	  if (when) {if (p == sc->F) break;} else {if (p != sc->F) break;}
	  for (p = if_true; is_pair(cdr(p)); p = cdr(p))
	    fx_call(sc, p);
	  sc->rec_p1 = fx_call(sc, la);
	  slot_set_value(laa_slot, fx_call(sc, laa));
	  slot_set_value(la_slot, sc->rec_p1);
	  sc->curlet = outer_let;
	  slot_set_value(let_slot, fx_call(sc, let_var));
	  sc->curlet = inner_let;
	}
    }
  unstack(sc);
  free_cell(sc, let_slots(inner_let));
  free_cell(sc, inner_let);
  sc->value = sc->unspecified;
}

