typedef enum {SL_NO_FIELD=0, SL_STACK_TOP, SL_STACK_SIZE, SL_STACKTRACE_DEFAULTS, SL_HEAP_SIZE, SL_FREE_HEAP_SIZE,
	      SL_GC_FREED, SL_GC_PROTECTED_OBJECTS, SL_GC_TOTAL_FREED, SL_GC_INFO, SL_FILE_NAMES, SL_ROOTLET_SIZE, SL_C_TYPES, SL_SAFETY,
	      SL_UNDEFINED_IDENTIFIER_WARNINGS, SL_UNDEFINED_CONSTANT_WARNINGS, SL_GC_STATS, SL_MAX_HEAP_SIZE,
	      SL_MAX_PORT_DATA_SIZE, SL_MAX_STACK_SIZE, SL_CPU_TIME, SL_CATCHES, SL_STACK, SL_MAX_STRING_LENGTH,
	      SL_MAX_FORMAT_LENGTH, SL_MAX_LIST_LENGTH, SL_MAX_VECTOR_LENGTH, SL_MAX_VECTOR_DIMENSIONS,
	      SL_DEFAULT_HASH_TABLE_LENGTH, SL_INITIAL_STRING_PORT_LENGTH, SL_DEFAULT_RATIONALIZE_ERROR,
	      SL_DEFAULT_RANDOM_STATE, SL_EQUIVALENT_FLOAT_EPSILON, SL_HASH_TABLE_FLOAT_EPSILON, SL_PRINT_LENGTH,
	      SL_BIGNUM_PRECISION, SL_MEMORY_USAGE, SL_FLOAT_FORMAT_PRECISION, SL_HISTORY, SL_HISTORY_ENABLED,
	      SL_HISTORY_SIZE, SL_PROFILE, SL_PROFILE_INFO, SL_AUTOLOADING, SL_ACCEPT_ALL_KEYWORD_ARGUMENTS,
	      SL_MOST_POSITIVE_FIXNUM, SL_MOST_NEGATIVE_FIXNUM, SL_OUTPUT_PORT_DATA_SIZE, SL_DEBUG, SL_VERSION,
	      SL_GC_TEMPS_SIZE, SL_GC_RESIZE_HEAP_FRACTION, SL_GC_RESIZE_HEAP_BY_4_FRACTION, SL_OPENLETS,
	      SL_NUM_FIELDS} s7_let_field_t;

static const char *s7_let_field_names[SL_NUM_FIELDS] =
  {"no-field", "stack-top", "stack-size", "stacktrace-defaults", "heap-size", "free-heap-size",
   "gc-freed", "gc-protected-objects", "gc-total-freed", "gc-info", "file-names", "rootlet-size", "c-types", "safety",
   "undefined-identifier-warnings", "undefined-constant-warnings", "gc-stats", "max-heap-size",
   "max-port-data-size", "max-stack-size", "cpu-time", "catches", "stack", "max-string-length",
   "max-format-length", "max-list-length", "max-vector-length", "max-vector-dimensions",
   "default-hash-table-length", "initial-string-port-length", "default-rationalize-error",
   "default-random-state", "equivalent-float-epsilon", "hash-table-float-epsilon", "print-length",
   "bignum-precision", "memory-usage", "float-format-precision", "history", "history-enabled",
   "history-size", "profile", "profile-info", "autoloading?", "accept-all-keyword-arguments",
   "most-positive-fixnum", "most-negative-fixnum", "output-port-data-size", "debug", "version",
   "gc-temps-size", "gc-resize-heap-fraction", "gc-resize-heap-by-4-fraction", "openlets"};

static s7_int s7_let_length(void) {return(SL_NUM_FIELDS - 1);}

static s7_pointer s7_let_add_field(s7_scheme *sc, const char *name, s7_let_field_t field)
{
  s7_pointer sym;
  sym = make_symbol(sc, name);
  symbol_set_s7_let(sym, field);
  return(sym);
}

static void init_s7_let(s7_scheme *sc)
{
  int32_t i;
  for (i = SL_STACK_TOP; i < SL_NUM_FIELDS; i++)
    s7_let_add_field(sc, s7_let_field_names[i], (s7_let_field_t)i);
}

/* handling all *s7* fields via fallbacks lets us use direct field accesses in the rest of s7, and avoids
 *   using ca 100 cells for the let slots/values.  We would need the fallbacks anyway for 'files et al.
 *   Since most of the fields need special setters, it's actually less code this way.  See old/s7-let-s7.c.
 */

#if (!MS_WINDOWS)
  #include <sys/resource.h>
#endif

static s7_pointer kmg(s7_scheme *sc, s7_int bytes)
{
  block_t *b;
  int len = 0;
  b = mallocate(sc, 128);
  if (bytes < 1000)
    len = snprintf((char *)block_data(b), 128, "%" print_s7_int, bytes);
  else
    {
      if (bytes < 1000000)
	len = snprintf((char *)block_data(b), 128, "%.1fk", bytes / 1000.0);
      else
	{
	  if (bytes < 1000000000)
	    len = snprintf((char *)block_data(b), 128, "%.1fM", bytes / 1000000.0);
	  else len = snprintf((char *)block_data(b), 128, "%.1fG", bytes / 1000000000.0);
	}
    }
  check_print_length(len, 128);
  return(cons(sc, make_integer(sc, bytes), block_to_string(sc, b, len)));
}

static s7_pointer memory_usage(s7_scheme *sc)
{
  s7_int gc_loc, i, k, len, in_use = 0, vlen = 0, flen = 0, ilen = 0, blen = 0, hlen = 0;
  s7_pointer mu_let;
  gc_list *gp;
  s7_int ts[NUM_TYPES];

#if (!MS_WINDOWS)
  struct rusage info;
  struct timeval ut;
#endif

  mu_let = s7_inlet(sc, sc->nil);
  gc_loc = s7_gc_protect_1(sc, mu_let);

#if (!MS_WINDOWS)
  getrusage(RUSAGE_SELF, &info);
  ut = info.ru_utime;
  make_slot_1(sc, mu_let, make_symbol(sc, "process-time"), make_real(sc, ut.tv_sec + (floor(ut.tv_usec / 1000.0) / 1000.0)));
#ifdef __APPLE__
  make_slot_1(sc, mu_let, make_symbol(sc, "process-resident-size"), kmg(sc, info.ru_maxrss));
  /* apple docs say this is in kilobytes, but apparently that is an error */
#else
  make_slot_1(sc, mu_let, make_symbol(sc, "process-resident-size"), kmg(sc, info.ru_maxrss * 1024));
#endif
  make_slot_1(sc, mu_let, make_symbol(sc, "IO"), cons(sc, make_integer(sc, info.ru_inblock), make_integer(sc, info.ru_oublock)));
#endif

#if (!S7_DEBUGGING)
  make_slot_1(sc, mu_let,
	      make_symbol(sc, "permanent-cells"),
	      cons(sc, make_integer(sc, (sc->permanent_cells + NUM_SMALL_INTS)), kmg(sc, (sc->permanent_cells + NUM_SMALL_INTS) * sizeof(s7_cell))));
#else
  make_slot_1(sc, mu_let, make_symbol(sc, "permanent-cells"),
	      s7_list(sc, 14,
		      make_integer(sc, (sc->permanent_cells + NUM_SMALL_INTS)),
		      kmg(sc, (sc->permanent_cells + NUM_SMALL_INTS) * sizeof(s7_cell)), /* big allocs here, so this is slightly inaccurate */
		      make_symbol(sc, "unheap"), make_integer(sc, (petrified_pointers + NUM_SMALL_INTS)),
		      make_symbol(sc, "slot"),   make_integer(sc, permanent_slots),
		      make_symbol(sc, "port"),   make_integer(sc, permanent_ports),
		      make_symbol(sc, "string"), make_integer(sc, permanent_strings),
		      make_symbol(sc, "cons"),   make_integer(sc, permanent_conses),
		      make_symbol(sc, "func"),   make_integer(sc, permanent_functions)));
#endif
  make_slot_1(sc, mu_let, make_symbol(sc, "rootlet-size"), make_integer(sc, sc->rootlet_entries));
  make_slot_1(sc, mu_let, make_symbol(sc, "heap-size"), cons(sc, make_integer(sc, sc->heap_size), kmg(sc, sc->heap_size * (sizeof(s7_cell) + 2 * sizeof(s7_pointer)))));
  make_slot_1(sc, mu_let, make_symbol(sc, "cell-size"), make_integer(sc, sizeof(s7_cell)));

  /* show how many active cells there are of each type */
  for (i = 0; i < NUM_TYPES; i++) ts[i] = 0;
  for (k = 0; k < sc->heap_size; k++)
    ts[unchecked_type(sc->heap[k])]++;
  sc->w = sc->nil;
  for (i = 0; i < NUM_TYPES; i++)
    {
      if (i > 0) in_use += ts[i];
      if (ts[i] > 50)
	sc->w = cons(sc, cons(sc, make_symbol(sc, (i == 0) ? "free" : type_name_from_type(i, NO_ARTICLE)), make_integer(sc, ts[i])), sc->w);
    }
  make_slot_1(sc, mu_let, make_symbol(sc, "cells-in-use/free"), cons(sc, make_integer(sc, in_use), make_integer(sc, sc->free_heap_top - sc->free_heap)));
  if (is_pair(sc->w))
    make_slot_1(sc, mu_let, make_symbol(sc, "types"), sc->w);
  sc->w = sc->nil;

  make_slot_1(sc, mu_let, make_symbol(sc, "gc-protected-objects"),
	      cons(sc, make_integer(sc, sc->protected_objects_size - sc->gpofl_loc),
		   make_integer(sc, sc->protected_objects_size)));
  make_slot_1(sc, mu_let, make_symbol(sc, "setters"), make_integer(sc, sc->protected_setters_loc));

  /* check the symbol table, counting gensyms etc */
  {
    s7_int syms = 0, gens = 0, keys = 0, mx_list = 0;
    for (i = 0; i < SYMBOL_TABLE_SIZE; i++)
      {
	s7_pointer x;
	for (k = 0, x = vector_element(sc->symbol_table, i); is_not_null(x); x = cdr(x), k++)
	  {
	    syms++;
	    if (is_gensym(car(x))) gens++;
	    if (is_keyword(car(x))) keys++;
	  }
	if (k > mx_list) mx_list = k;
      }
    make_slot_1(sc, mu_let, make_symbol(sc, "symbol-table"),
		s7_list(sc, 9,
			make_integer(sc, SYMBOL_TABLE_SIZE),
			make_symbol(sc, "max-bin"), make_integer(sc, mx_list),
			make_symbol(sc, "symbols"), cons(sc, make_integer(sc, syms), make_integer(sc, syms - gens - keys)),
			make_symbol(sc, "gensyms"), make_integer(sc, gens),
			make_symbol(sc, "keys"),    make_integer(sc, keys)));
  }
  make_slot_1(sc, mu_let, make_symbol(sc, "stack"), cons(sc, make_integer(sc, s7_stack_top(sc)), make_integer(sc, sc->stack_size)));

  len = sc->autoload_names_top * (sizeof(const char **) + sizeof(s7_int) + sizeof(bool));
  for (i = 0; i < sc->autoload_names_loc; i++) len += sc->autoload_names_sizes[i];
  make_slot_1(sc, mu_let, make_symbol(sc, "autoload"), make_integer(sc, len));

  make_slot_1(sc, mu_let, make_symbol(sc, "circle_info"), make_integer(sc, sc->circle_info->size * (sizeof(s7_pointer) + sizeof(int32_t) + sizeof(bool))));

  /* check the gc lists (finalizations) */
  len = sc->strings->size + sc->vectors->size + sc->input_ports->size + sc->output_ports->size + sc->input_string_ports->size +
    sc->continuations->size + sc->c_objects->size + sc->hash_tables->size + sc->gensyms->size + sc->unknowns->size +
    sc->lambdas->size + sc->multivectors->size + sc->weak_refs->size + sc->weak_hash_iterators->size + sc->lamlets->size;
  make_slot_1(sc, mu_let, make_symbol(sc, "gc-lists"), cons(sc, make_integer(sc, len), make_integer(sc, len * sizeof(s7_pointer))));

  /* strings */
  gp = sc->strings;
  for (len = 0, i = 0; i < (int32_t)(gp->loc); i++)
    len += string_length(gp->list[i]);
  make_slot_1(sc, mu_let, make_symbol(sc, "strings"), cons(sc, make_integer(sc, gp->loc), make_integer(sc, len)));

  /* vectors */
  for (k = 0, gp = sc->vectors; k < 2; k++)
    {
      for (i = 0; i < gp->loc; i++)
	{
	  s7_pointer v;
	  v = gp->list[i];
	  if (is_float_vector(v))
	    flen += vector_length(v);
	  else
	    {
	      if (is_int_vector(v))
		ilen += vector_length(v);
	      else
		{
		  if (is_byte_vector(v))
		    blen += vector_length(v);
		  else vlen += vector_length(v);
		}
	    }
	}
      gp = sc->multivectors;
    }
  make_slot_1(sc, mu_let, make_symbol(sc, "vectors"),
	      s7_list(sc, 9,
		      make_integer(sc, sc->vectors->loc + sc->multivectors->loc),
		      make_symbol(sc, "vlen"),  make_integer(sc, vlen),
		      make_symbol(sc, "fvlen"), make_integer(sc, flen),
		      make_symbol(sc, "ivlen"), make_integer(sc, ilen),
		      make_symbol(sc, "bvlen"), make_integer(sc, blen)));

  /* hash-tables */
  for (i = 0, gp = sc->hash_tables; i < gp->loc; i++)
    {
      s7_pointer v;
      v = gp->list[i];
      hlen += ((hash_table_mask(v) + 1) * sizeof(hash_entry_t *));
      hlen += (hash_table_entries(v) * sizeof(hash_entry_t));
    }
  make_slot_1(sc, mu_let, make_symbol(sc, "hash-tables"), cons(sc, make_integer(sc, sc->hash_tables->loc), make_integer(sc, hlen)));

  /* ports */
  gp = sc->input_ports;
  for (i = 0, len = 0; i < gp->loc; i++)
    {
      s7_pointer v;
      v = gp->list[i];
      if (port_data(v)) len += port_data_size(v);
    }
  gp = sc->input_string_ports;
  for (i = 0, len = 0; i < gp->loc; i++)
    {
      s7_pointer v;
      v = gp->list[i];
      if (port_data(v)) len += port_data_size(v);
    }
  make_slot_1(sc, mu_let, make_symbol(sc, "input-ports"), cons(sc, make_integer(sc, sc->input_ports->loc + sc->input_string_ports->loc), make_integer(sc, len)));

  gp = sc->output_ports;
  for (i = 0, len = 0; i < gp->loc; i++)
    {
      s7_pointer v;
      v = gp->list[i];
      if (port_data(v)) len += port_data_size(v);
    }
  make_slot_1(sc, mu_let, make_symbol(sc, "output-ports"), cons(sc, make_integer(sc, sc->output_ports->loc), make_integer(sc, len)));

  /* continuations (sketchy!) */
  gp = sc->continuations;
  for (i = 0, len = 0; i < gp->loc; i++)
    if (is_continuation(gp->list[i]))
      len += continuation_stack_size(gp->list[i]);
  if (len > 0)
    make_slot_1(sc, mu_let, make_symbol(sc, "continuations"), cons(sc, make_integer(sc, sc->continuations->loc), make_integer(sc, len * sizeof(s7_pointer))));

  /* c-objects */
  if (sc->c_objects->loc > 0)
    make_slot_1(sc, mu_let, make_symbol(sc, "c-objects"), make_integer(sc, sc->c_objects->loc));
#if WITH_GMP
  make_slot_1(sc, mu_let, make_symbol(sc, "bignums"),
	      s7_list(sc, 5, make_integer(sc, sc->big_integers->loc), make_integer(sc, sc->big_ratios->loc),
		      make_integer(sc, sc->big_reals->loc), make_integer(sc, sc->big_complexes->loc),
		      make_integer(sc, sc->big_random_states->loc)));
#endif

  /* free-lists (mallocate) */
  {
    block_t *b;
    for (i = 0, len = 0, sc->w = sc->nil; i < TOP_BLOCK_LIST; i++)
      {
	for (b = sc->block_lists[i], k = 0; b; b = block_next(b), k++);
	sc->w = cons(sc, make_integer(sc, k), sc->w);
	len += ((sizeof(block_t) + (1LL << i)) * k);
      }
    for (b = sc->block_lists[TOP_BLOCK_LIST], k = 0; b; b = block_next(b), k++)
      len += (sizeof(block_t) + block_size(b));
    sc->w = cons(sc, make_integer(sc, k), sc->w);
    make_slot_1(sc, mu_let, make_symbol(sc, "free-lists"),
		list_2(sc, cons(sc, make_symbol(sc, "bytes"), kmg(sc, len)),
		       cons(sc, make_symbol(sc, "bins"), safe_reverse_in_place(sc, sc->w))));
    sc->w = sc->nil;

    make_slot_1(sc, mu_let, make_symbol(sc, "approximate-s7-size"),
		kmg(sc, ((sc->permanent_cells + NUM_SMALL_INTS + sc->heap_size) * sizeof(s7_cell)) +
		    ((2 * sc->heap_size + SYMBOL_TABLE_SIZE + sc->stack_size) * sizeof(s7_pointer)) +
		    len + hlen +
		    (vlen * sizeof(s7_pointer)) + (flen * sizeof(s7_double)) + (ilen * sizeof(s7_int)) + blen));
  }

  s7_gc_unprotect_at(sc, gc_loc);
  return(mu_let);
}

static s7_pointer sl_c_types(s7_scheme *sc)
{
  s7_pointer res;
  int32_t i;
  sc->w = sc->nil;
  for (i = 0; i < sc->num_c_object_types; i++)                       /*   c-object type (tag) is i */
    sc->w = cons(sc, sc->c_object_types[i]->scheme_name, sc->w);
  res = safe_reverse_in_place(sc, sc->w);                            /*   so car(types) has tag 0 */
  sc->w = sc->nil;
  return(res);
}

static s7_pointer sl_file_names(s7_scheme *sc)
{
  int32_t i;
  s7_pointer p;
  sc->w = sc->nil;
  for (i = 0; i <= sc->file_names_top; i++)
    sc->w = cons(sc, sc->file_names[i], sc->w);
  p = safe_reverse_in_place(sc, sc->w);
  sc->w = sc->nil;
  return(p);
}

static s7_pointer sl_int_fixup(s7_scheme *sc, s7_pointer val)
{
#if WITH_GMP
  return(s7_int_to_big_integer(sc, s7_integer(val)));
#else
  return(val);
#endif
}

static s7_pointer sl_history(s7_scheme *sc)
  {
#if WITH_HISTORY
    return(cull_history(sc, (sc->cur_code == sc->history_sink) ? sc->old_cur_code : sc->cur_code));
#else
    return(sc->cur_code);
#endif
}

static s7_pointer s7_let_field(s7_scheme *sc, s7_pointer sym)
{
  switch (symbol_s7_let(sym))
    {
    case SL_ACCEPT_ALL_KEYWORD_ARGUMENTS:  return(make_boolean(sc, sc->accept_all_keyword_arguments));
    case SL_AUTOLOADING:                   return(s7_make_boolean(sc, sc->is_autoloading));
    case SL_BIGNUM_PRECISION:              return(make_integer(sc, sc->bignum_precision));
    case SL_CATCHES:                       return(active_catches(sc));
    case SL_CPU_TIME:                      return(s7_make_real(sc, (double)clock() / (double)CLOCKS_PER_SEC));
    case SL_C_TYPES:                       return(sl_c_types(sc));
    case SL_DEBUG:                         return(make_integer(sc, sc->debug));
    case SL_DEFAULT_HASH_TABLE_LENGTH:     return(make_integer(sc, sc->default_hash_table_length));
    case SL_DEFAULT_RANDOM_STATE:          return(sc->default_rng);
    case SL_DEFAULT_RATIONALIZE_ERROR:     return(make_real(sc, sc->default_rationalize_error));
    case SL_EQUIVALENT_FLOAT_EPSILON:      return(s7_make_real(sc, sc->equivalent_float_epsilon));
    case SL_FILE_NAMES:                    return(sl_file_names(sc));
    case SL_FLOAT_FORMAT_PRECISION:        return(make_integer(sc, sc->float_format_precision));
    case SL_FREE_HEAP_SIZE:                return(make_integer(sc, sc->free_heap_top - sc->free_heap));
    case SL_GC_FREED:                      return(make_integer(sc, sc->gc_freed));
    case SL_GC_TOTAL_FREED:                return(make_integer(sc, sc->gc_total_freed));
    case SL_GC_INFO:                       return(list_3(sc, make_integer(sc, sc->gc_calls), make_integer(sc, sc->gc_total_time), make_integer(sc, ticks_per_second())));
    case SL_GC_PROTECTED_OBJECTS:          return(sc->protected_objects);
    case SL_GC_STATS:                      return(make_integer(sc, sc->gc_stats));
    case SL_GC_TEMPS_SIZE:                 return(make_integer(sc, sc->gc_temps_size));
    case SL_GC_RESIZE_HEAP_FRACTION:       return(make_real(sc, sc->gc_resize_heap_fraction));
    case SL_GC_RESIZE_HEAP_BY_4_FRACTION:  return(make_real(sc, sc->gc_resize_heap_by_4_fraction));
    case SL_HASH_TABLE_FLOAT_EPSILON:      return(s7_make_real(sc, sc->hash_table_float_epsilon));
    case SL_HEAP_SIZE:                     return(make_integer(sc, sc->heap_size));
    case SL_HISTORY:                       return(sl_history(sc));
    case SL_HISTORY_ENABLED:               return(s7_make_boolean(sc, s7_history_enabled(sc)));
    case SL_HISTORY_SIZE:                  return(make_integer(sc, sc->history_size));
    case SL_INITIAL_STRING_PORT_LENGTH:    return(make_integer(sc, sc->initial_string_port_length));
    case SL_MAX_FORMAT_LENGTH:             return(make_integer(sc, sc->max_format_length));
    case SL_MAX_HEAP_SIZE:                 return(make_integer(sc, sc->max_heap_size));
    case SL_MAX_LIST_LENGTH:               return(make_integer(sc, sc->max_list_length));
    case SL_MAX_PORT_DATA_SIZE:            return(make_integer(sc, sc->max_port_data_size));
    case SL_MAX_STACK_SIZE:                return(make_integer(sc, sc->max_stack_size));
    case SL_MAX_STRING_LENGTH:             return(make_integer(sc, sc->max_string_length));
    case SL_MAX_VECTOR_DIMENSIONS:         return(make_integer(sc, sc->max_vector_dimensions));
    case SL_MAX_VECTOR_LENGTH:             return(make_integer(sc, sc->max_vector_length));
    case SL_MEMORY_USAGE:                  return(memory_usage(sc));
    case SL_MOST_NEGATIVE_FIXNUM:          return(sl_int_fixup(sc, leastfix));
    case SL_MOST_POSITIVE_FIXNUM:          return(sl_int_fixup(sc, mostfix));
    case SL_OPENLETS:                      return(s7_make_boolean(sc, sc->has_openlets));
    case SL_OUTPUT_PORT_DATA_SIZE:         return(make_integer(sc, sc->output_port_data_size));
    case SL_PRINT_LENGTH:                  return(make_integer(sc, sc->print_length));
    case SL_PROFILE:                       return(make_integer(sc, sc->profile));
    case SL_PROFILE_INFO:                  return(profile_info_out(sc));
    case SL_ROOTLET_SIZE:                  return(make_integer(sc, sc->rootlet_entries));
    case SL_SAFETY:                        return(make_integer(sc, sc->safety));
    case SL_STACK:                         return(stack_entries(sc, sc->stack, s7_stack_top(sc)));
    case SL_STACKTRACE_DEFAULTS:           return(sc->stacktrace_defaults);
    case SL_STACK_SIZE:                    return(make_integer(sc, sc->stack_size));
    case SL_STACK_TOP:                     return(make_integer(sc, (sc->stack_end - sc->stack_start) / 4));
    case SL_UNDEFINED_CONSTANT_WARNINGS:   return(s7_make_boolean(sc, sc->undefined_constant_warnings));
    case SL_UNDEFINED_IDENTIFIER_WARNINGS: return(s7_make_boolean(sc, sc->undefined_identifier_warnings));
    case SL_VERSION:                       return(s7_make_string(sc, "s7 " S7_VERSION ", " S7_DATE));
    default:
      return(s7_error(sc, sc->out_of_range_symbol, set_elist_2(sc, wrap_string(sc, "can't get (*s7* '~S); no such field in *s7*", 43), sym)));
    }
  return(sc->undefined);
}

s7_pointer s7_let_field_ref(s7_scheme *sc, s7_pointer sym)
{
  if (is_symbol(sym))
    {
      if (is_keyword(sym))
	sym = keyword_symbol(sym);
      if (symbol_s7_let(sym) != SL_NO_FIELD)
	return(s7_let_field(sc, sym));
    }
  return(sc->undefined);
}

static s7_pointer g_s7_let_ref_fallback(s7_scheme *sc, s7_pointer args)
{
  s7_pointer sym;

  sym = cadr(args);
  if (!is_symbol(sym))
    return(simple_wrong_type_argument(sc, sc->let_ref_symbol, sym, T_SYMBOL));
  if (is_keyword(sym))
    sym = keyword_symbol(sym);
  return(s7_let_field(sc, sym));
}

static s7_pointer s7_let_iterate(s7_scheme *sc, s7_pointer iterator)
{
  s7_pointer symbol, value, osw;

  iterator_position(iterator)++;
  if (iterator_position(iterator) >= SL_NUM_FIELDS)
    return(iterator_quit(iterator));

  symbol = make_symbol(sc, s7_let_field_names[iterator_position(iterator)]);
  osw = sc->w;  /* protect against s7_let_field list making */
  value = s7_let_field(sc, symbol);
  sc->w = osw;

  if (iterator_let_cons(iterator))
    {
      s7_pointer p;
      p = iterator_let_cons(iterator);
      set_car(p, symbol);
      set_cdr(p, value);
      return(p);
    }
  return(cons(sc, symbol, value));
}

static s7_pointer s7_let_make_iterator(s7_scheme *sc, s7_pointer iter)
{
  iterator_position(iter) = SL_NO_FIELD;
  iterator_next(iter) = s7_let_iterate;
  iterator_let_cons(iter) = NULL;
  return(iter);
}

static s7_pointer sl_real_geq_0(s7_scheme *sc, s7_pointer sym, s7_pointer val)
{
  if (!s7_is_real(val))
    return(simple_wrong_type_argument(sc, sym, val, T_REAL));
  if (s7_real(val) < 0.0)
    return(simple_out_of_range(sc, sym, val, wrap_string(sc, "should be positive", 18)));
  return(val);
}

static s7_pointer sl_integer_gt_0(s7_scheme *sc, s7_pointer sym, s7_pointer val)
{
  if (!s7_is_integer(val))
    return(simple_wrong_type_argument(sc, sym, val, T_INTEGER));
  if (s7_integer(val) <= 0)
    return(simple_out_of_range(sc, sym, val, wrap_string(sc, "should be positive", 18)));
  return(val);
}

static s7_pointer sl_integer_geq_0(s7_scheme *sc, s7_pointer sym, s7_pointer val)
{
  if (!s7_is_integer(val))
    return(simple_wrong_type_argument(sc, sym, val, T_INTEGER));
  if (s7_integer(val) < 0)
    return(simple_out_of_range(sc, sym, val, wrap_string(sc, "should be non-negative", 22)));
  return(val);
}

#if WITH_HISTORY
static void sl_set_history_size(s7_scheme *sc, s7_int iv)
{
  s7_pointer p1, p2, p3;
  if (iv > MAX_HISTORY_SIZE) iv = MAX_HISTORY_SIZE;
  if (iv > sc->true_history_size)
    {
      /* splice in the new cells, reattach the circles */
      s7_pointer next1, next2, next3;
      next1 = cdr(sc->eval_history1);
      next2 = cdr(sc->eval_history2);
      next3 = cdr(sc->history_pairs);
      set_cdr(sc->eval_history1, permanent_list(sc, iv - sc->true_history_size));
      set_cdr(sc->eval_history2, permanent_list(sc, iv - sc->true_history_size));
      set_cdr(sc->history_pairs, permanent_list(sc, iv - sc->true_history_size));
      for (p3 = cdr(sc->history_pairs); is_pair(cdr(p3)); p3 = cdr(p3)) set_car(p3, permanent_list(sc, 1));
      set_car(p3, permanent_list(sc, 1));
      set_cdr(p3, next3);
      for (p1 = sc->eval_history1, p2 = sc->eval_history2; is_pair(cdr(p1)); p1 = cdr(p1), p2 = cdr(p2));
      set_cdr(p1, next1);
      set_cdr(p2, next2);
      sc->true_history_size = iv;
    }
  sc->history_size = iv;
  /* clear out both bufffers to avoid GC confusion */
  for (p1 = sc->eval_history1, p2 = sc->eval_history2; ; p2 = cdr(p2))
    {
      set_car(p1, sc->nil);
      set_car(p2, sc->nil);
      p1 = cdr(p1);
      if (p1 == sc->eval_history1) break;
    }
}
#endif

#if WITH_GMP
static s7_pointer set_bignum_precision(s7_scheme *sc, int32_t precision)
{
  mp_prec_t bits;
  if (precision <= 1)                   /* (set! (*s7* 'bignum-precision) 1) causes mpfr to segfault! (also 0 and -1) */
    return(s7_out_of_range_error(sc, "set! (*s7* 'bignum-precision)", 0, wrap_integer2(sc, precision), "has to be greater than 1"));
  bits = (mp_prec_t)precision;
  mpfr_set_default_prec(bits);
  mpc_set_default_precision(bits);
  s7_symbol_set_value(sc, sc->pi_symbol, big_pi(sc));
  return(sc->F);
}
#endif

static s7_pointer sl_unsettable_error(s7_scheme *sc, s7_pointer sym)
{
  return(s7_error(sc, sc->immutable_error_symbol, set_elist_2(sc, wrap_string(sc, "can't set (*s7* '~S)", 20), sym)));
}

static s7_pointer g_s7_let_set_fallback(s7_scheme *sc, s7_pointer args)
{
  s7_pointer sym, val;

  sym = cadr(args);
  if (!is_symbol(sym))
    return(simple_wrong_type_argument(sc, sc->let_set_symbol, sym, T_SYMBOL));
  if (is_keyword(sym))
    sym = keyword_symbol(sym);
  val = caddr(args);

  switch (symbol_s7_let(sym))
    {
    case SL_ACCEPT_ALL_KEYWORD_ARGUMENTS:
      if (s7_is_boolean(val)) {sc->accept_all_keyword_arguments = s7_boolean(sc, val); return(val);}
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));

    case SL_AUTOLOADING:
      if (s7_is_boolean(val)) {sc->is_autoloading = s7_boolean(sc, val); return(val);}
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));

    case SL_BIGNUM_PRECISION:
      {
	s7_int iv;
	iv = s7_integer(sl_integer_gt_0(sc, sym, val));
	sc->bignum_precision = iv;
#if WITH_GMP
	set_bignum_precision(sc, sc->bignum_precision);
	mpfr_set_prec(sc->mpfr_1, sc->bignum_precision);
	mpfr_set_prec(sc->mpfr_2, sc->bignum_precision);
	mpc_set_prec(sc->mpc_1, sc->bignum_precision);
	mpc_set_prec(sc->mpc_2, sc->bignum_precision);
#endif
	return(val);
      }

    case SL_CATCHES:  return(sl_unsettable_error(sc, sym));
    case SL_CPU_TIME: return(sl_unsettable_error(sc, sym));
    case SL_C_TYPES:  return(sl_unsettable_error(sc, sym));

    case SL_DEBUG:
      if (s7_is_integer(val))
	{
	  sc->debug = s7_integer(val);
	  sc->debug_or_profile = ((sc->debug  > 1) || (sc->profile > 0));
	  if ((sc->debug > 0) &&
	      (!is_memq(make_symbol(sc, "debug.scm"), s7_symbol_value(sc, sc->features_symbol))))
	    s7_load(sc, "debug.scm");
	  return(val);
	}
      return(simple_wrong_type_argument(sc, sym, val, T_INTEGER));

    case SL_DEFAULT_HASH_TABLE_LENGTH:
      sc->default_hash_table_length = s7_integer(sl_integer_gt_0(sc, sym, val));
      return(val);

    case SL_DEFAULT_RANDOM_STATE:
      if (is_random_state(val))
	{
#if (!WITH_GMP)
	  random_seed(sc->default_rng) = random_seed(val);
	  random_carry(sc->default_rng) = random_carry(val);
#endif
	  return(val);
	}
      return(wrong_type_argument_with_type(sc, sym, 1, val, a_random_state_object_string));

    case SL_DEFAULT_RATIONALIZE_ERROR:
      sc->default_rationalize_error = s7_real(sl_real_geq_0(sc, sym, val));
      return(val);

    case SL_EQUIVALENT_FLOAT_EPSILON:
      sc->equivalent_float_epsilon = s7_real(sl_real_geq_0(sc, sym, val));
      return(val);

    case SL_FILE_NAMES:
      return(sl_unsettable_error(sc, sym));

    case SL_FLOAT_FORMAT_PRECISION:
      {
	s7_int iv; /* float-format-precision should not be huge => hangs in snprintf -- what's a reasonable limit here? */
	iv = s7_integer(sl_integer_geq_0(sc, sym, val));
	sc->float_format_precision = (iv < MAX_FLOAT_FORMAT_PRECISION) ? iv : MAX_FLOAT_FORMAT_PRECISION;
	return(val);
      }

    case SL_FREE_HEAP_SIZE:       return(sl_unsettable_error(sc, sym));
    case SL_GC_FREED:             return(sl_unsettable_error(sc, sym));
    case SL_GC_TOTAL_FREED:       return(sl_unsettable_error(sc, sym));
    case SL_GC_PROTECTED_OBJECTS: return(sl_unsettable_error(sc, sym));
    case SL_GC_TEMPS_SIZE:                sc->gc_temps_size = s7_integer(sl_integer_gt_0(sc, sym, val)); return(val);
    case SL_GC_RESIZE_HEAP_FRACTION:      sc->gc_resize_heap_fraction = s7_real(sl_real_geq_0(sc, sym, val)); return(val);
    case SL_GC_RESIZE_HEAP_BY_4_FRACTION: sc->gc_resize_heap_by_4_fraction = s7_real(sl_real_geq_0(sc, sym, val)); return(val);

    case SL_GC_STATS:
      if (s7_is_boolean(val)) {sc->gc_stats = ((val == sc->T) ? GC_STATS : 0); return(val);}
      if (s7_is_integer(val)) {sc->gc_stats = s7_integer(val); return(val);}
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));
    case SL_GC_INFO:
      if (val == sc->F)
	{
	  sc->gc_total_time = 0;
	  sc->gc_calls = 0;
	  return(sc->F);
	}
      return(simple_wrong_type_argument_with_type(sc, sym, val, wrap_string(sc, "#f (to clear gc_calls and gc_total_time)", 40)));

    case SL_HASH_TABLE_FLOAT_EPSILON:
      sc->hash_table_float_epsilon = s7_real(sl_real_geq_0(sc, sym, val));
      return(val);

    case SL_HEAP_SIZE:
      {
	s7_int iv;
	iv = s7_integer(sl_integer_geq_0(sc, sym, val));
	if (iv > sc->heap_size)
	  resize_heap_to(sc, iv);
	return(val);
      }

    case SL_HISTORY:               /* (set! (*s7* 'history) val) */
      replace_current_code(sc, val);
      return(val);

    case SL_HISTORY_ENABLED:       /* (set! (*s7* 'history-enabled) #f|#t) */
      if (s7_is_boolean(val))
	return(s7_make_boolean(sc, s7_set_history_enabled(sc, s7_boolean(sc, val))));
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));

    case SL_HISTORY_SIZE:
      {
	s7_int iv;
	iv = s7_integer(sl_integer_geq_0(sc, sym, val));
#if WITH_HISTORY
	sl_set_history_size(sc, iv);
#else
	sc->history_size = iv;
#endif
	return(val);
      }

    case SL_INITIAL_STRING_PORT_LENGTH: sc->initial_string_port_length = s7_integer(sl_integer_gt_0(sc, sym, val)); return(val);
    case SL_MAX_FORMAT_LENGTH:          sc->max_format_length = s7_integer(sl_integer_gt_0(sc, sym, val));          return(val);
    case SL_MAX_HEAP_SIZE:              sc->max_heap_size = s7_integer(sl_integer_gt_0(sc, sym, val));              return(val);
    case SL_MAX_LIST_LENGTH:            sc->max_list_length = s7_integer(sl_integer_gt_0(sc, sym, val));            return(val);
    case SL_MAX_PORT_DATA_SIZE:         sc->max_port_data_size = s7_integer(sl_integer_gt_0(sc, sym, val));         return(val);

    case SL_MAX_STACK_SIZE:
      {
	s7_int iv;
	iv = s7_integer(sl_integer_geq_0(sc, sym, val));
	if (iv < INITIAL_STACK_SIZE)
	  return(simple_out_of_range(sc, sym, val, wrap_string(sc, "should be greater than the initial stack size", 45)));
	sc->max_stack_size = (uint32_t)iv;
	return(val);
      }

    case SL_MAX_STRING_LENGTH:     sc->max_string_length = s7_integer(sl_integer_gt_0(sc, sym, val));     return(val);
    case SL_MAX_VECTOR_DIMENSIONS: sc->max_vector_dimensions = s7_integer(sl_integer_gt_0(sc, sym, val)); return(val);
    case SL_MAX_VECTOR_LENGTH:     sc->max_vector_length = s7_integer(sl_integer_gt_0(sc, sym, val));     return(val);
    case SL_MEMORY_USAGE:          return(sl_unsettable_error(sc, sym));
    case SL_MOST_NEGATIVE_FIXNUM:  return(sl_unsettable_error(sc, sym));
    case SL_MOST_POSITIVE_FIXNUM:  return(sl_unsettable_error(sc, sym));

    case SL_OPENLETS:
      if (s7_is_boolean(val)) {sc->has_openlets = s7_boolean(sc, val); return(val);}
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));

    case SL_OUTPUT_PORT_DATA_SIZE: sc->output_port_data_size = s7_integer(sl_integer_gt_0(sc, sym, val)); return(val);
    case SL_PRINT_LENGTH:          sc->print_length = s7_integer(sl_integer_geq_0(sc, sym, val));         return(val);

    case SL_PROFILE:
      if (s7_is_integer(val))
	{
	  sc->profile = s7_integer(val);
	  sc->debug_or_profile = ((sc->debug  > 1) || (sc->profile > 0));
	  if (sc->profile > 0)
	    {
	      if (!is_memq(make_symbol(sc, "profile.scm"), s7_symbol_value(sc, sc->features_symbol)))
		s7_load(sc, "profile.scm");
	      if (!sc->profile_data)
		make_profile_info(sc);
	      if (!sc->profile_out)
		sc->profile_out = s7_make_function(sc, "profile-out", g_profile_out, 2, 0, false, NULL);
	    }
	  return(val);
	}
      return(simple_wrong_type_argument(sc, sym, val, T_INTEGER));

    case SL_PROFILE_INFO:
      if (val == sc->F)
	return(clear_profile_info(sc));
      return(simple_wrong_type_argument_with_type(sc, sym, val, wrap_string(sc, "#f (to clear the table)", 23)));

    case SL_ROOTLET_SIZE: return(sl_unsettable_error(sc, sym));

    case SL_SAFETY:
      if (s7_is_integer(val))
	{
	  if ((s7_integer(val) > 2) || (s7_integer(val) < -1))
	    return(simple_out_of_range(sc, sym, val, wrap_string(sc, "should be between -1 (no safety) and 2 (max safety)", 51)));
	  sc->safety = s7_integer(val);
	  return(val);
	}
      return(simple_wrong_type_argument(sc, sym, val, T_INTEGER));

    case SL_STACKTRACE_DEFAULTS:
      if (!is_pair(val))
	return(simple_wrong_type_argument(sc, sym, val, T_PAIR));
      if (s7_list_length(sc, val) != 5)
	return(simple_wrong_type_argument_with_type(sc, sym, val, wrap_string(sc, "a list with 5 entries", 21)));
      if (!is_t_integer(car(val)))
	return(wrong_type_argument_with_type(sc, sym, 1, car(val), wrap_string(sc, "an integer (stack frames)", 25)));
      if (!is_t_integer(cadr(val)))
	return(wrong_type_argument_with_type(sc, sym, 2, cadr(val), wrap_string(sc, "an integer (cols-for-data)", 26)));
      if (!is_t_integer(caddr(val)))
	return(wrong_type_argument_with_type(sc, sym, 3, caddr(val), wrap_string(sc, "an integer (line length)", 24)));
      if (!is_t_integer(cadddr(val)))
	return(wrong_type_argument_with_type(sc, sym, 4, cadddr(val), wrap_string(sc, "an integer (comment position)", 29)));
      if (!s7_is_boolean(s7_list_ref(sc, val, 4)))
	return(wrong_type_argument_with_type(sc, sym, 5, s7_list_ref(sc, val, 4), wrap_string(sc, "a boolean (treat-data-as-comment)", 33)));
      sc->stacktrace_defaults = copy_proper_list(sc, val);
      return(val);

    case SL_STACK:
    case SL_STACK_SIZE:
    case SL_STACK_TOP:  return(sl_unsettable_error(sc, sym));

    case SL_UNDEFINED_CONSTANT_WARNINGS:
      if (s7_is_boolean(val)) {sc->undefined_constant_warnings = s7_boolean(sc, val); return(val);}
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));

    case SL_UNDEFINED_IDENTIFIER_WARNINGS:
      if (s7_is_boolean(val)) {sc->undefined_identifier_warnings = s7_boolean(sc, val); return(val);}
      return(simple_wrong_type_argument(sc, sym, val, T_BOOLEAN));

    case SL_VERSION:  return(sl_unsettable_error(sc, sym));

    default:
      return(s7_error(sc, sc->out_of_range_symbol, set_elist_2(sc, wrap_string(sc, "can't set (*s7* '~S); no such field in *s7*", 43), sym)));
    }
  return(sc->undefined);
}

s7_pointer s7_let_field_set(s7_scheme *sc, s7_pointer sym, s7_pointer new_value)
{
  if (is_symbol(sym))
    {
      if (is_keyword(sym))
	sym = keyword_symbol(sym);
      if (symbol_s7_let(sym) != SL_NO_FIELD)
	return(g_s7_let_set_fallback(sc, set_plist_3(sc, sc->s7_let_symbol, sym, new_value)));
    }
  return(sc->undefined);
}


